import { Component, OnInit, Inject, ViewChild } from '@angular/core';
import { UntypedFormControl, Validators } from '@angular/forms';
import {
  MatLegacyDialog as MatDialog,
  MatLegacyDialogRef as MatDialogRef,
  MAT_LEGACY_DIALOG_DATA as MAT_DIALOG_DATA,
} from '@angular/material/legacy-dialog';
import { MatLegacyOption as MatOption } from '@angular/material/legacy-core';
import { MatLegacySelect as MatSelect } from '@angular/material/legacy-select';
import { MyErrorStateMatcher } from 'src/app/utils/ErrorStateMatcher';
import { generateUniqueID } from 'src/app/utils/Utils';
import { ConfirmModalComponent } from '../../confirm-modal/confirm-modal.component';
import { MoveGroupMembersComponent } from '../move-group-members/move-group-members.component';
import { GroupService } from 'src/app/services/group.service';
import { Group } from 'src/app/models/group';
import { MatLegacyTabChangeEvent as MatTabChangeEvent } from '@angular/material/legacy-tabs';
import { SnackbarService } from 'src/app/services/snackbar.service';

@Component({
  selector: 'app-manage-group-modal',
  templateUrl: './manage-group-modal.component.html',
  styleUrls: ['./manage-group-modal.component.scss'],
})
export class ManageGroupModalComponent implements OnInit {
  @ViewChild('selectGroups') select: MatSelect;
  @ViewChild(MoveGroupMembersComponent) child: MoveGroupMembersComponent;

  groups: Group[] = [];
  selectedGroups: Group[] = [];
  updatedGroups: Group[];
  showEdit = false;
  editGroup: Group;
  selectedTab: Number = 0;
  groupsHasChanged = false;

  groupNameControl = new UntypedFormControl('', [Validators.required]);
  editNameControl = new UntypedFormControl('', [Validators.required]);
  matcher = new MyErrorStateMatcher();

  constructor(
    private groupService: GroupService,
    private dialog: MatDialog,
    private dialogRef: MatDialogRef<ManageGroupModalComponent>,
    private snackbarService: SnackbarService,
    @Inject(MAT_DIALOG_DATA) public data: any
  ) {}

  ngOnInit(): void {
    this.initializeGroups();
    this.dialogRef.disableClose = true;
    this.dialogRef.backdropClick().subscribe(() => {
      this.handleCloseConfirm();
    });
  }

  private initializeGroups(): void {
    this.groups = [];
    this.groupService
      .getByProjectId(this.data.project.projectID)
      .then((groups) => {
        if (groups) this.groups.push(...groups);
      });
  }

  updateGroups(groups: Group[]): void {
    this.updatedGroups = [];
    this.updatedGroups.push(...groups);
  }

  groupsChanged(changed: boolean): void {
    this.groupsHasChanged = changed;
  }

  saveGroups(): void {
    const promises: Promise<Group>[] = [];
    this.updatedGroups.forEach((group) => {
      promises.push(this.groupService.update(group.groupID, group));
      if (this.editGroup && this.editGroup.groupID === group.groupID)
        this.editGroup = group;
      this.groups.forEach((elem, index) => {
        if (elem.groupID === group.groupID) this.groups[index] = group;
      });
    });
    Promise.all(promises)
      .then(() => {
        this.snackbarService.queueSnackbar('Successfully saved groups');
      })
      .catch((error) => {
        this.snackbarService.queueSnackbar(error.data.message);
      });
    this.groupsChanged(false);
  }

  updateEditGroupMembers(group: Group): void {
    this.editGroup.members = group.members;
  }

  openEdit(group: Group): void {
    this.showEdit = true;
    this.editGroup = group;
  }

  closeEdit(): void {
    this.showEdit = false;
  }

  closeModal(): void {
    this.dialogRef.close();
  }

  handleCloseConfirm(): void {
    if (this.groupsHasChanged) {
      this.dialog.open(ConfirmModalComponent, {
        width: '500px',
        data: {
          title: 'Confirmation',
          message: 'You have unsaved changes',
          confirmLabel: 'Save groups',
          cancelLabel: 'Close without saving',
          handleConfirm: async () => {
            this.saveGroups();
          },
          handleCancel: async () => {
            this.closeModal();
          },
        },
      });
    } else {
      this.closeModal();
    }
  }

  tabChanged(tabChangeEvent: MatTabChangeEvent): void {
    this.selectedTab = tabChangeEvent.index;
  }

  /* --- CRUD operations --- */

  async updateGroup(group: Group) {
    await this.groupService.update(group.groupID, group);
    this.closeEdit();
    this.groups.forEach((elem, index) => {
      if (elem.groupID === group.groupID) this.groups[index] = group;
    });
  }

  async createGroup() {
    const group: Group = {
      groupID: generateUniqueID(),
      projectID: this.data.project.projectID,
      name: this.groupNameControl.value,
      members: [],
    };
    await this.groupService.create(group);
    this.groupNameControl.reset();
    this.groups.push(group);
  }

  async deleteGroup(group: Group) {
    this.dialog.open(ConfirmModalComponent, {
      width: '500px',
      data: {
        title: 'Confirmation',
        message: 'Are you sure you want to permanently delete this group?',
        handleConfirm: async () => {
          await this.groupService.delete(group.groupID);
          this.groups.forEach((obj, index) => {
            if (obj.groupID == group.groupID) this.groups.splice(index, 1);
          });
        },
      },
    });
  }
}
<h1 mat-dialog-title>Manage Groups</h1>
<div mat-dialog-content>
  <mat-tab-group dynamicHeight animationDuration="0ms" (selectedTabChange)="tabChanged($event)">
    <mat-tab label="Groups">
      <div
        *ngIf="showEdit"
        style="margin-top: 20px"
      >
        <h4>Enter a new group name:</h4>
        <mat-form-field
          appearance="outline"
          style="width: 100%;"
        >
          <mat-label>Group Name</mat-label>
          <input
            name="editName"
            [formControl]="editNameControl"
            [errorStateMatcher]="matcher"
            [(ngModel)]="editGroup.name"
            matInput
            autocomplete="off"
            cdkFocusInitial
          />
          <mat-error *ngIf="!editNameControl.value">Group Name is <strong>required</strong></mat-error>
        </mat-form-field>
        <app-move-group-members
          [projectID]="data.project.projectID"
          [groups]="[editGroup]"
          [headerText]="'Select students to add to your group:'"
          (updateGroups)="updateEditGroupMembers($event[0])"
        >
        </app-move-group-members>
      </div>
      <mat-list *ngIf="!showEdit">
        <div class="create-group-container">
          <mat-form-field
            appearance="outline"
            class="create-group-form"
          >
            <mat-label>Group Name</mat-label>
            <input
              name="groupName"
              [formControl]="groupNameControl"
              [errorStateMatcher]="matcher"
              matInput
              autocomplete="off"
              cdkFocusInitial
            />
            <mat-error *ngIf="!groupNameControl.value">Group Name is <strong>required</strong></mat-error>
          </mat-form-field>
          <button
            type="submit"
            [disabled]="!groupNameControl.valid"
            mat-button
            class="create-group-submit"
            (click)="createGroup()"
            cdkFocusInitial
          >
            Create
          </button>
        </div>

        <div mat-subheader>Groups: {{ groups.length }}</div>
        <mat-list-item *ngFor="let group of groups">
          <mat-icon mat-list-icon>group</mat-icon>
          <div mat-line>{{ group.name }}</div>
          <button 
            mat-icon-button
            matTooltip="Edit Group"
            (click)="openEdit(group)"
          >
            <mat-icon>edit</mat-icon>
          </button>
          <button 
            mat-icon-button
            matTooltip="Delete Group"
            (click)="deleteGroup(group)"
          >
            <mat-icon>delete</mat-icon>
          </button>
        </mat-list-item>
      </mat-list>
    </mat-tab>
    <mat-tab label="Multi-Group Assign">
      <mat-form-field
        appearance="fill"
        style="margin-top: 20px;"
      >
        <mat-label>Select Groups...</mat-label>
        <mat-select
          #selectGroups
          [(ngModel)]="selectedGroups"
          multiple
        >
          <mat-option
            *ngFor="let group of groups"
            [value]="group"
          >
            ({{ group.members.length }}) {{ group.name }}
          </mat-option>
        </mat-select>
      </mat-form-field>
      <app-move-group-members
        [groups]="selectedGroups"
        [projectID]="data.project.projectID"
        [headerText]="'Move students manually with drag&drop or randomly using the shuffle button:'"
        (updateGroups)="updateGroups($event)"
        (groupsChanged)="groupsChanged($event)"
      >
      </app-move-group-members>
    </mat-tab>
  </mat-tab-group>
</div>
<div mat-dialog-actions>
  <div *ngIf="showEdit && selectedTab === 0" >
    <button 
      mat-button
      matTooltip="Back"
      (click)="closeEdit()"
    >
      Back
    </button>
    <button
      type="submit"
      [disabled]="!editNameControl.valid"
      mat-button
      cdkFocusInitial
      (click)="updateGroup(this.editGroup)"
    >
      Update
    </button>
  </div>
  <div *ngIf="!showEdit && selectedTab === 0">
    <button 
      mat-button
      matTooltip="Back"
      (click)="closeModal ()"
    >
      Close
    </button>
  </div>
  <div class="multi-group-actions" *ngIf="selectedTab === 1" >
    <button
      mat-icon-button
      matTooltip="Shuffle members between selected groups"
      (click)="child.shuffleBetweenGroups()"
    >
      Shuffle <mat-icon>shuffle</mat-icon>
    </button>
    <div>
      <button
        mat-button
        (click)="closeModal()"
      >
        Close without saving
      </button>
      <button
        mat-button
        (click)="saveGroups()"
      >
        Save Groups
      </button>
    </div>
  </div>
</div>
