import { Component, OnInit, Inject, Input, OnChanges } from '@angular/core';
import {
  MatLegacyDialogRef as MatDialogRef,
  MAT_LEGACY_DIALOG_DATA as MAT_DIALOG_DATA,
  MatLegacyDialog as MatDialog,
} from '@angular/material/legacy-dialog';
import { TodoItemService } from 'src/app/services/todoItem.service';
import { GroupService } from 'src/app/services/group.service';
import { TodoItem, CompletionQuality } from 'src/app/models/todoItem';
import {
  EXPANDED_TODO_TYPE,
  TODO_TYPE_COLORS,
  EXPANDED_COMPLETION_QUALITY,
} from 'src/app/utils/constants';
import { MatLegacyTableDataSource as MatTableDataSource } from '@angular/material/legacy-table';
import { SelectionModel } from '@angular/cdk/collections';
import { AddTodoListModalComponent } from '../add-todo-list-modal/add-todo-list-modal.component';
import { TodoItemCardModalComponent } from '../todo-item-card-modal/todo-item-card-modal.component';
import { ConfirmModalComponent } from '../confirm-modal/confirm-modal.component';
import { interval } from 'rxjs';
import { PausableObservable, pausable } from 'rxjs-pausable';
import { Group } from 'src/app/models/group';

@Component({
  selector: 'app-todo-list-modal',
  templateUrl: './todo-list-modal.component.html',
  styleUrls: ['./todo-list-modal.component.scss'],
})
export class TodoListModalComponent implements OnInit, OnChanges {
  minDate: Date;
  userGroups: Group[];
  personalTodoItems: TodoItem[];
  groupTodoItems: TodoItem[];
  todoItemsMap: Map<string, TodoItem>;
  personalDataSource: MatTableDataSource<TodoItem>;
  groupDataSource: MatTableDataSource<TodoItem>;
  groupIDtoGroupMap: object;

  selection = new SelectionModel<TodoItem>(true, []);
  pausable: PausableObservable<number>;
  completedItemsDataSource: MatTableDataSource<TodoItem>;
  CONFETTI_DELAY = 3400;
  CONFETTI_DURATION = 500;
  todoItemTypes = EXPANDED_TODO_TYPE;
  todoItemColors = TODO_TYPE_COLORS;
  EXPANDED_COMPLETION_QUALITY: typeof EXPANDED_COMPLETION_QUALITY =
    EXPANDED_COMPLETION_QUALITY;
  CompletionQuality: typeof CompletionQuality = CompletionQuality;
  displayColumns = ['select', 'task-title', 'type', 'deadline', 'edit'];
  displayGroupColumns = [
    'select',
    'task-title',
    'type',
    'group',
    'deadline',
    'edit',
  ];
  completedDisplayColums = [
    'task-title',
    'type',
    'group',
    'quality',
    'completion-date',
    'options',
  ];

  @Input()
  projectID: string;

  @Input()
  userID: string;

  constructor(
    public dialog: MatDialog,
    public todoItemService: TodoItemService,
    public groupService: GroupService
  ) {}

  async ngOnInit() {
    await this.getTodoItems();
    this.pausable = interval(800).pipe(
      pausable()
    ) as PausableObservable<number>;
    this.pausable.subscribe(this.shoot.bind(this));
    this.pausable.pause();
  }

  async ngOnChanges() {
    await this.getTodoItems();
  }

  async getTodoItems() {
    this.personalTodoItems = await this.todoItemService.getByUserProject(
      this.userID,
      this.projectID
    );

    this.userGroups = await this.groupService.getByUserAndProject(
      this.userID,
      this.projectID
    );

    this.groupIDtoGroupMap = this.userGroups.reduce(function (map, obj) {
      map[obj.groupID] = obj;
      return map;
    }, {});

    this.groupTodoItems = await this.todoItemService.getMultipleByGroup(
      this.userGroups.map((group) => group.groupID)
    );

    this.updateTableDataSource();
  }

  updateTableDataSource() {
    this.personalDataSource = new MatTableDataSource<TodoItem>(
      this.personalTodoItems.filter(
        (todoItem: TodoItem) => !todoItem.completed && !todoItem.groupID
      )
    );

    this.groupDataSource = new MatTableDataSource<TodoItem>(
      this.groupTodoItems.filter((todoItem: TodoItem) => !todoItem.completed)
    );

    this.todoItemsMap = new Map(
      this.personalTodoItems.map((todo) => [todo.todoItemID, todo])
    );

    this.completedItemsDataSource = new MatTableDataSource<TodoItem>(
      [...this.todoItemsMap.values()].filter(
        (todoItem: TodoItem) => todoItem.completed
      )
    );
  }

  isAllSelected(dataSource) {
    const numSelected = this.selection.selected.length;
    const numRows = dataSource.data.length;
    return numSelected === numRows;
  }

  masterToggle(dataSource) {
    this.isAllSelected(dataSource)
      ? this.selection.clear()
      : dataSource.data.forEach((row) => this.selection.select(row));
  }

  async completeTodoItem() {
    this.playAudio();
    setTimeout(() => {
      this.shoot();
      this.pausable.resume();
      setTimeout(() => this.pausable.pause(), this.CONFETTI_DURATION);
    }, this.CONFETTI_DELAY);
  }

  async handleDeleteTodoItems() {
    if (this.selection.selected.length > 0) {
      this.dialog.open(ConfirmModalComponent, {
        width: '500px',
        data: {
          title: 'Confirmation',
          message:
            'This will permanently delete the selected Todo Items. Are you sure you want to do this?',
          handleConfirm: async () => {
            for (let i = 0; i < this.selection.selected.length; i++) {
              await this.todoItemService.remove(
                this.selection.selected[i].todoItemID
              );
            }
            await this.getTodoItems();
          },
        },
      });
      ``;
    }
  }

  async deleteTodoItems() {
    this.selection.selected.forEach(async (todoItem) => {
      await this.todoItemService.remove(todoItem.todoItemID);
    });
    await this.getTodoItems();
  }

  openAddTodoItemModal() {
    this.dialog.open(AddTodoListModalComponent, {
      width: '600px',
      data: {
        projectID: this.projectID,
        userID: this.userID,
        onComplete: async (todoItem: TodoItem) => {
          let dataSource: MatTableDataSource<TodoItem>;
          if (todoItem.groupID) {
            this.groupTodoItems.push(todoItem);
            dataSource = this.groupDataSource;
          } else {
            dataSource = this.personalDataSource;
            this.personalTodoItems.push(todoItem);
          }
          const data = dataSource.data;
          data.push(todoItem);
          dataSource.data = data;
        },
      },
    });
  }

  async editTodoItem(todoItem: TodoItem) {
    this.dialog.open(AddTodoListModalComponent, {
      width: '600px',
      data: {
        projectID: this.projectID,
        userID: this.userID,
        todoItem: todoItem,
        group: todoItem.groupID
          ? await this.groupService.getById(todoItem.groupID)
          : null,
        onComplete: async (t?: TodoItem) => {
          if (t && t !== todoItem) {
            this.groupTodoItems = this.groupTodoItems.filter(
              (ti) => ti.todoItemID !== t.todoItemID
            );
            this.personalTodoItems = this.personalTodoItems.filter(
              (ti) => ti.todoItemID !== t.todoItemID
            );
            this.updateTableDataSource();
            if (t.groupID) {
              this.groupTodoItems.push(t);
            } else {
              this.personalTodoItems.push(t);
            }
            this.updateTableDataSource();
          }
        },
      },
    });
  }

  async openTodoItemViewModal(todoItem: TodoItem) {
    this.dialog.open(TodoItemCardModalComponent, {
      width: '500px',
      data: {
        todoItem: todoItem,
        projectID: this.projectID,
        userID: this.userID,
        group: todoItem.groupID
          ? await this.groupService.getById(todoItem.groupID)
          : null,
        onComplete: async (t: TodoItem) => {
          this.groupTodoItems = this.groupTodoItems.filter(
            (ti) => ti.todoItemID !== t.todoItemID
          );
          this.personalTodoItems = this.personalTodoItems.filter(
            (ti) => ti.todoItemID !== t.todoItemID
          );
          this.todoItemsMap.delete(todoItem.todoItemID);
          if (t.groupID) {
            this.groupTodoItems.push(t);
          } else {
            this.personalTodoItems.push(t);
          }
          this.updateTableDataSource();
          if (t.quality !== CompletionQuality.INCOMPLETE)
            await this.completeTodoItem();
        },
        onEdit: async (t: TodoItem) => {
          if (t && t !== todoItem) {
            this.groupTodoItems = this.groupTodoItems.filter(
              (ti) => ti.todoItemID !== t.todoItemID
            );
            this.personalTodoItems = this.personalTodoItems.filter(
              (ti) => ti.todoItemID !== t.todoItemID
            );
            this.updateTableDataSource();
            if (t.groupID) {
              this.groupTodoItems.push(t);
            } else {
              this.personalTodoItems.push(t);
            }
            this.updateTableDataSource();
          }
        },
        onRestore: async (t: TodoItem) => {
          if (t) {
            this.todoItemsMap.delete(todoItem.todoItemID);
            if (t.groupID) {
              this.groupTodoItems.push(t);
            } else {
              this.personalTodoItems.push(t);
            }
            this.updateTableDataSource();
          }
        },
      },
    });
  }

  restoreTodoItem(todoItem: TodoItem) {
    this.dialog.open(AddTodoListModalComponent, {
      width: '600px',
      data: {
        projectID: this.projectID,
        userID: this.userID,
        restoreTodoItem: todoItem,
        onComplete: async (t?: TodoItem) => {
          if (t) {
            this.todoItemsMap.delete(todoItem.todoItemID);
            if (t.groupID) {
              this.groupTodoItems.push(t);
            } else {
              this.personalTodoItems.push(t);
            }
            this.updateTableDataSource();
          }
        },
      },
    });
  }

  shoot() {
    try {
      this.confetti({
        angle: this.random(60, 120),
        spread: this.random(10, 50),
        particleCount: this.random(50, 60),
        origin: {
          y: 0.6,
        },
      });
    } catch (e) {
      // noop, confettijs may not be loaded yet
    }
  }

  playAudio() {
    const audio = new Audio();
    audio.src = '../../../assets/celebration-sound-effect.wav';
    audio.load();
    audio.play();
  }

  random(min: number, max: number) {
    return Math.random() * (max - min) + min;
  }

  confetti(args: any) {
    return window['confetti'].apply(this, arguments);
  }
}
<h1 mat-dialog-title>Todo List</h1>
<div style="position: relative">
  <mat-tab-group dynamicHeight animationDuration="0ms">
    <mat-tab label="Personal Todo List">
      <div class="mat-elevation-z3 table_style">
        <table mat-table [dataSource]="personalDataSource" class="table">
          <ng-container matColumnDef="select">
            <th mat-header-cell *matHeaderCellDef>
              <mat-checkbox
                (change)="$event ? masterToggle(personalDataSource) : null"
                [checked]="
                  selection.hasValue() && isAllSelected(personalDataSource)
                "
                [indeterminate]="
                  selection.hasValue() && !isAllSelected(personalDataSource)
                "
              >
              </mat-checkbox>
            </th>
            <td mat-cell *matCellDef="let row">
              <mat-checkbox
                (click)="$event.stopPropagation()"
                (change)="$event ? selection.toggle(row) : null"
                [checked]="selection.isSelected(row)"
              >
              </mat-checkbox>
            </td>
          </ng-container>

          <ng-container matColumnDef="task-title">
            <th mat-header-cell *matHeaderCellDef>Task Title</th>
            <td mat-cell *matCellDef="let todo">
              <a>{{ todo.title }} </a>
            </td>
          </ng-container>

          <ng-container matColumnDef="type">
            <th mat-header-cell class="table-type-header" *matHeaderCellDef> Task Type </th>
            <td mat-cell *matCellDef="let todo"> 
              <mat-chip
                class="chip-base"
                *ngFor="let type of todo.type"
                [ngStyle]="{
                  'background-color': todoItemColors[type],
                  margin: '2px'
                }"
              >
                {{ todoItemTypes[type] }}
              </mat-chip>
            </td>
          </ng-container>

          <ng-container matColumnDef="deadline">
            <th mat-header-cell *matHeaderCellDef>Deadline</th>
            <td mat-cell *matCellDef="let todo">
              {{ todo.deadline.date }} @ {{ todo.deadline.time }}
            </td>
          </ng-container>

          <ng-container matColumnDef="edit">
            <th mat-header-cell *matHeaderCellDef></th>
            <td mat-cell *matCellDef="let todo">
              <button
                mat-icon-button
                (click)="editTodoItem(todo); $event.stopPropagation()"
                matTooltip="Edit Task"
              >
                <mat-icon>more_vert</mat-icon>
              </button>
            </td>
          </ng-container>

          <tr mat-header-row *matHeaderRowDef="displayColumns"></tr>
          <tr
            mat-row
            class="todoItemRow"
            (click)="openTodoItemViewModal(row)"
            *matRowDef="let row; columns: displayColumns"
          ></tr>
        </table>
      </div>
      <mat-chip-list style="display: flex; margin-top: 10px; height: 60px">
        <mat-chip
          mat-raised-button
          color="warn"
          (click)="handleDeleteTodoItems()"
          style="text-align: center; margin-right: 10px"
          selected
        >
          <mat-icon class="chip-icon" style="width: 0px; overflow: initial"
            >delete</mat-icon
          >
          <span style="width: 100%; text-align: center">
            Delete Todo Items
          </span>
        </mat-chip>
        <button
          mat-mini-fab
          color="accent"
          matTooltip="Add Todo Item"
          matTooltipPosition="before"
          class="addTodoItemButton"
          (click)="openAddTodoItemModal()"
        >
          <mat-icon class="addTodoItemIcon">add</mat-icon>
        </button>
      </mat-chip-list>
    </mat-tab>
    <mat-tab label="Group Todo List">
      <div class="mat-elevation-z3 table_style">
        <table mat-table [dataSource]="groupDataSource" class="table">
          <ng-container matColumnDef="select">
            <th mat-header-cell *matHeaderCellDef>
              <mat-checkbox
                (change)="$event ? masterToggle(groupDataSource) : null"
                [checked]="
                  selection.hasValue() && isAllSelected(groupDataSource)
                "
                [indeterminate]="
                  selection.hasValue() && !isAllSelected(groupDataSource)
                "
              >
              </mat-checkbox>
            </th>
            <td mat-cell *matCellDef="let row">
              <mat-checkbox
                (click)="$event.stopPropagation()"
                (change)="$event ? selection.toggle(row) : null"
                [checked]="selection.isSelected(row)"
              >
              </mat-checkbox>
            </td>
          </ng-container>

          <ng-container matColumnDef="task-title">
            <th mat-header-cell *matHeaderCellDef>Task Title</th>
            <td mat-cell *matCellDef="let todo">
              <a>{{ todo.title }} </a>
            </td>
          </ng-container>

          <ng-container matColumnDef="type">
            <th mat-header-cell *matHeaderCellDef>Task Type</th>
            <td mat-cell *matCellDef="let todo">
              <mat-chip
                class="chip-base"
                *ngFor="let type of todo.type"
                [ngStyle]="{
                  'background-color': todoItemColors[type],
                  margin: '2px'
                }"
              >
                {{ todoItemTypes[type] }}
              </mat-chip>
            </td>
          </ng-container>

          <ng-container matColumnDef="group">
            <th mat-header-cell *matHeaderCellDef>Group Name</th>
            <td mat-cell *matCellDef="let todo">
              <mat-chip class="chip-base" style="background-color: #63e388">
                {{ groupIDtoGroupMap[todo.groupID].name }}
              </mat-chip>
            </td>
          </ng-container>

          <ng-container matColumnDef="deadline">
            <th mat-header-cell *matHeaderCellDef>Deadline</th>
            <td mat-cell *matCellDef="let todo">
              {{ todo.deadline.date }} @ {{ todo.deadline.time }}
            </td>
          </ng-container>

          <ng-container matColumnDef="edit">
            <th mat-header-cell *matHeaderCellDef></th>
            <td mat-cell *matCellDef="let todo">
              <button
                mat-icon-button
                (click)="editTodoItem(todo); $event.stopPropagation()"
                matTooltip="Edit Task"
              >
                <mat-icon>more_vert</mat-icon>
              </button>
            </td>
          </ng-container>

          <tr mat-header-row *matHeaderRowDef="displayGroupColumns"></tr>
          <tr
            mat-row
            class="todoItemRow"
            *matRowDef="let row; columns: displayGroupColumns"
            (click)="openTodoItemViewModal(row)"
          ></tr>
        </table>
      </div>
      <mat-chip-list style="display: flex; margin-top: 10px; height: 60px">
        <mat-chip
          mat-raised-button
          color="warn"
          (click)="handleDeleteTodoItems()"
          style="text-align: center; margin-right: 10px"
          selected
        >
          <mat-icon class="chip-icon" style="width: 0px; overflow: initial"
            >delete</mat-icon
          >
          <span style="width: 100%; text-align: center">
            Delete Todo Items
          </span>
        </mat-chip>
        <button
          mat-mini-fab
          color="accent"
          matTooltip="Add Todo Item"
          matTooltipPosition="before"
          class="addTodoItemButton"
          (click)="openAddTodoItemModal()"
        >
          <mat-icon class="addTodoItemIcon">add</mat-icon>
        </button>
      </mat-chip-list>
    </mat-tab>
    <mat-tab label="Completed Tasks">
      <div class="mat-elevation-z3 table_style">
        <table mat-table [dataSource]="completedItemsDataSource" class="table">
          <ng-container matColumnDef="task-title">
            <th mat-header-cell *matHeaderCellDef>Task Title</th>
            <td mat-cell *matCellDef="let todo">
              <a>{{ todo.title }} </a>
            </td>
          </ng-container>

          <ng-container matColumnDef="completion-date">
            <th mat-header-cell *matHeaderCellDef>Completion Date</th>
            <td mat-cell *matCellDef="let todo">
              {{ todo.deadline.date }} @ {{ todo.deadline.time }}
            </td>
          </ng-container>

          <ng-container matColumnDef="type">
            <th mat-header-cell *matHeaderCellDef>Task Type</th>
            <td mat-cell *matCellDef="let todo">
              <mat-chip
                class="chip-base"
                *ngFor="let type of todo.type"
                [ngStyle]="{
                  'background-color': todoItemColors[type],
                  margin: '2px'
                }"
              >
                {{ todoItemTypes[type] }}
              </mat-chip>
            </td>
          </ng-container>

          <ng-container matColumnDef="group">
            <th mat-header-cell *matHeaderCellDef>Category</th>
            <td mat-cell *matCellDef="let todo">
              <mat-chip
                class="chip-base"
                *ngIf="todo.groupID"
                style="background-color: #63e388"
              >
                Group: {{ groupIDtoGroupMap[todo.groupID].name }}
              </mat-chip>
              <mat-chip
                class="chip-base"
                *ngIf="!todo.groupID"
                style="background-color: #f8391d"
              >
                Personal
              </mat-chip>
            </td>
          </ng-container>

          <ng-container matColumnDef="quality">
            <th mat-header-cell *matHeaderCellDef>Quality</th>
            <td
              mat-cell
              *matCellDef="let todo"
              [ngStyle]="{
                color:
                  todo.quality === CompletionQuality.INCOMPLETE
                    ? 'red'
                    : 'green'
              }"
            >
              {{ EXPANDED_COMPLETION_QUALITY[todo.quality] }}
            </td>
          </ng-container>

          <ng-container matColumnDef="options">
            <th mat-header-cell *matHeaderCellDef></th>
            <td mat-cell *matCellDef="let todo">
              <button
                mat-icon-button
                (click)="restoreTodoItem(todo); $event.stopPropagation()"
                matTooltip="Restore Task"
              >
                <mat-icon>more_vert</mat-icon>
              </button>
            </td>
          </ng-container>

          <tr mat-header-row *matHeaderRowDef="completedDisplayColums"></tr>
          <tr
            mat-row
            class="todoItemRow"
            *matRowDef="let row; columns: completedDisplayColums"
            (click)="openTodoItemViewModal(row)"
          ></tr>
        </table>
      </div>
    </mat-tab>
  </mat-tab-group>
</div>
