import { ComponentType } from '@angular/cdk/portal';
import { Component, Input, OnInit } from '@angular/core';
import { MatLegacyDialog as MatDialog } from '@angular/material/legacy-dialog';
import { Router } from '@angular/router';
import { Board, BoardScope } from 'src/app/models/board';
import { Project } from 'src/app/models/project';
import { AuthUser, Role } from 'src/app/models/user';
import { UserService } from 'src/app/services/user.service';
import { CreateWorkflowModalComponent } from '../create-workflow-modal/create-workflow-modal.component';
import { ListModalComponent } from '../list-modal/list-modal.component';
import { ManageGroupModalComponent } from '../groups/manage-group-modal/manage-group-modal.component';
import { ConfigurationModalComponent } from '../configuration-modal/configuration-modal.component';
import { BucketsModalComponent } from '../buckets-modal/buckets-modal.component';

@Component({
  selector: 'app-toolbar-menu',
  templateUrl: './toolbar-menu.component.html',
  styleUrls: ['./toolbar-menu.component.scss'],
})
export class ToolbarMenuComponent implements OnInit {
  user: AuthUser;

  @Input()
  board: Board;

  @Input()
  project: Project;

  Role: typeof Role = Role;
  BoardScope: typeof BoardScope = BoardScope;

  constructor(
    private userService: UserService,
    private dialog: MatDialog,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.user = this.userService.user!;
  }

  openWorkflowDialog() {
    this._openDialog(CreateWorkflowModalComponent, {
      board: this.board,
      project: this.project,
    });
  }

  showListModal() {
    this._openDialog(
      ListModalComponent,
      {
        board: this.board,
        user: this.user,
        // TODO: configure for this.canvas.getCenter().left
        centerX: 0,
        // TODO: configure for this.canvas.getCenter().top
        centerY: 0,
      },
      '95vw'
    );
  }

  openSettingsDialog() {
    this._openDialog(ConfigurationModalComponent, {
      project: this.project,
      board: this.board,
      update: (board: Board, removed = false) => {
        const previousBoard = this.board;
        this.board = board;

        if (previousBoard.initialZoom !== board.initialZoom) {
          // this.configureZoom(); TODO: emit to parent
        }
      },
    });
  }

  openProjectTodoList() {
    this.router.navigate([`/project/${this.project.projectID}/todo`]);
  }

  openGroupDialog() {
    this.dialog.open(ManageGroupModalComponent, {
      data: {
        project: this.project,
      },
    });
  }

  showBucketsModal() {
    this._openDialog(
      BucketsModalComponent,
      {
        board: this.board,
        user: this.user,
        centerX: 0,
        centerY: 0,
      },
      '95vw'
    );
  }

  signOut(): void {
    this.userService.logout();
    this.router.navigate(['login']);
  }

  private _openDialog(
    component: ComponentType<unknown>,
    data: any,
    width = '700px'
  ) {
    this.dialog.open(component, {
      maxWidth: 1280,
      width: width,
      autoFocus: false,
      data: data,
    });
  }
}
<button navbarMenu mat-icon-button [matMenuTriggerFor]="navbarMenu">
    <mat-icon>dehaze</mat-icon>
</button>
<mat-menu #navbarMenu="matMenu">
    <button mat-menu-item *ngIf="user.role === Role.TEACHER && !!board && board.scope === BoardScope.PROJECT_SHARED" (click)="openWorkflowDialog()">
        <mat-icon>timeline</mat-icon>
        Manage Buckets & Workflows
    </button>
    <button mat-menu-item *ngIf="user.role === Role.TEACHER" (click)="showListModal()">
        <mat-icon>list_alt</mat-icon>
        List Posts
    </button>
    <button mat-menu-item *ngIf="user && user.role === Role.TEACHER && !!project" (click)="openProjectTodoList()">
        <mat-icon>assignment_ind</mat-icon>
        View All Todo Lists
    </button>
    <button mat-menu-item *ngIf="user.role === Role.TEACHER && !!board && board.scope === BoardScope.PROJECT_SHARED" (click)="openGroupDialog()">
        <mat-icon>group_work</mat-icon>
        Manage Groups
    </button>
    <button mat-menu-item *ngIf="user.role === Role.TEACHER" (click)="openSettingsDialog()">
        <mat-icon>settings</mat-icon>
        Adjust Settings
    </button>
    <!-- <button mat-menu-item *ngIf="!(user.role === Role.STUDENT && !board.permissions.showBucketStudent) && board.scope === BoardScope.PROJECT_SHARED" (click)="showBucketsModal()">
        <mat-icon>shopping_basket</mat-icon>
        View Buckets
    </button> -->
    <a href="https://score.oise.utoronto.ca/" style="text-decoration:none;color:black" target="_blank">
        <button mat-menu-item style="text-decoration:none;color:black">
            <mat-icon>launch</mat-icon>
            Go to SCORE
        </button>
    </a>
    <button mat-menu-item (click)="signOut()">
        <mat-icon class="material-icons-outlined">logout</mat-icon>
        Log Out
    </button>
</mat-menu>
