    return this.distributionWorkflowType === DistributionWorkflowType.RANDOM && this.postsPerBucket > 0 || this.distributionWorkflowType === DistributionWorkflowType.UPVOTES && this.upvoteLimit || this.distributionWorkflowType === DistributionWorkflowType.TAG && this.selectedTag;
  }
  _distributionWorkflowTypeData() {
    if (this.distributionWorkflowType === DistributionWorkflowType.RANDOM) return this.postsPerBucket;else if (this.distributionWorkflowType === DistributionWorkflowType.UPVOTES) return this.upvoteLimit;else return this.selectedTag;
  }
  _assembleDistributionWorkflow() {
    const workflowID = generateUniqueID();
    const workflow = {
      workflowID: workflowID,
      boardID: this.board.boardID,
      active: false,
      name: this.workflowName,
      source: this._mapToContainer(this.distributionSource),
      destinations: this._mapToContainers(this.distributionDestinations),
      distributionWorkflowType: {
        type: this.distributionWorkflowType,
        data: this._distributionWorkflowTypeData()
      },
      removeFromSource: this.removeFromSource
