import { Component, Inject, OnInit } from '@angular/core';
import {
  AbstractControl,
  UntypedFormControl,
  ValidationErrors,
  ValidatorFn,
  Validators,
} from '@angular/forms';
import {
  MatLegacyDialogRef as MatDialogRef,
  MAT_LEGACY_DIALOG_DATA as MAT_DIALOG_DATA,
  MatLegacyDialog as MatDialog,
} from '@angular/material/legacy-dialog';
import { MatLegacySnackBarConfig as MatSnackBarConfig } from '@angular/material/legacy-snack-bar';
import { Board, BoardScope } from 'src/app/models/board';
import { Tag } from 'src/app/models/tag';
import Bucket from 'src/app/models/bucket';
import { Group } from 'src/app/models/group';
import {
  Container,
  ContainerType,
  DistributionWorkflow,
  DistributionWorkflowType,
  TaskAction,
  TaskActionType,
  TaskWorkflow,
  WorkflowType,
  TaskWorkflowType,
} from 'src/app/models/workflow';
import { BoardService } from 'src/app/services/board.service';
import { BucketService } from 'src/app/services/bucket.service';
import { CanvasService } from 'src/app/services/canvas.service';
import { GroupService } from 'src/app/services/group.service';
import { SnackbarService } from 'src/app/services/snackbar.service';
import { WorkflowService } from 'src/app/services/workflow.service';
import { MyErrorStateMatcher } from 'src/app/utils/ErrorStateMatcher';
import { generateUniqueID } from 'src/app/utils/Utils';
import { ConfirmModalComponent } from '../confirm-modal/confirm-modal.component';

@Component({
  selector: 'app-create-workflow-modal',
  templateUrl: './create-workflow-modal.component.html',
  styleUrls: ['./create-workflow-modal.component.scss'],
})
export class CreateWorkflowModalComponent implements OnInit {
  // Properties
  selected = new UntypedFormControl(0); // Controls which tab is currently selected (0: Buckets, 1: Create, 2: Manage)

  // Data models
  board: Board; // Current board
  buckets: Bucket[]; // All buckets
  boardBuckets: Bucket[]; // Buckets associated with the current board
  workflows: any[] = []; // List of workflows
  tags: Tag[]; // Available tags for the board
  upvoteLimit: number; // Upvote limit for the board
  selectedTag: string; // Selected tag for filtering (if applicable)

  bucketName = '';
  workflowName = '';
  showDelete = false;

  // Common fields between all workflows
  WorkflowType: typeof WorkflowType = WorkflowType;
  taskWorkflowType: typeof TaskWorkflowType = TaskWorkflowType;
  workflowType: WorkflowType = WorkflowType.GENERATION;
  sourceOptions: (Bucket | Board)[] = [];
  destOptions: (Bucket | Board)[] = [];

  // Fields for distribution workflow creation
  distributionSource: Board | Bucket;
  distributionDestinations: (Bucket | Board)[] = [];
  postsPerBucket: number;
  distributionWorkflowType: DistributionWorkflowType =
    DistributionWorkflowType.RANDOM;
  removeFromSource = false;

  // Fields for peer review workflow and generation task workflow creation
  groupOptions: Group[] = [];
  taskSource: Board | Bucket;
  taskDestination: Board | Bucket;
  prompt: string;
  assignedGroups: Group[] = [];
  commentsRequired = false;
  tagsRequired = false;
  postGeneration = 1;

  bucketNameFormControl = new UntypedFormControl('valid', [
    Validators.required,
    this._forbiddenNameValidator(),
  ]);
  workflowNameFormControl = new UntypedFormControl('valid', [Validators.required]);
  sourceFormControl = new UntypedFormControl('valid', [Validators.required]);
  destinationFormControl = new UntypedFormControl('valid', [Validators.required]);

  sourceDestinationMatchError = new UntypedFormControl(false);

  groupsFormControl = new UntypedFormControl('valid', [Validators.required]);
  promptFormControl = new UntypedFormControl('valid', [Validators.required]);

  workflowTypeFormControl = new UntypedFormControl('valid', [Validators.required]);
  removeFromSourceFormControl = new UntypedFormControl('valid', [Validators.required]);

  tagsFormControl = new UntypedFormControl();

  matcher = new MyErrorStateMatcher();
  snackbarConfig: MatSnackBarConfig;

  constructor(
    public dialogRef: MatDialogRef<CreateWorkflowModalComponent>,
    public dialog: MatDialog,
    private snackbarService: SnackbarService,
    public bucketService: BucketService,
    public boardService: BoardService,
    public workflowService: WorkflowService,
    public canvasService: CanvasService,
    public groupService: GroupService,
    @Inject(MAT_DIALOG_DATA) public data: any
  ) {
    this.snackbarConfig = new MatSnackBarConfig();
    this.snackbarConfig.duration = 5000;
  }

  ngOnInit(): void {
    // Initialization
    this.board = this.data.board; // Load the current board from the passed data
    this.tags = this.data.board.tags; // Load tags associated with the board
    this.loadGroups(); // Load groups associated with the project
    this.upvoteLimit = this.data.board.upvoteLimit; // Load the upvote limit for the board
    this.loadBucketsBoards(); // Load buckets and boards for source/destination options
    this.loadWorkflows(); // Load existing workflows for the board
  }

  // Fetches groups for the project
  async loadGroups() {
    this.groupOptions = await this.groupService.getByProjectId(
      this.data.project.projectID
    );
  }

  // Loads buckets and boards, updates source/destination options.
  async loadBucketsBoards(): Promise<void> {
    this.sourceOptions = [];
    this.destOptions = [];
    this.boardBuckets = [];

    try {
      // 1. Fetch Project Boards
      const projectBoards = await this.boardService.getMultipleBy(
        this.data.project.boards,
        {
          scope: BoardScope.PROJECT_SHARED,
        }
      );

      // Add project boards FIRST
      this.destOptions = this.destOptions.concat(projectBoards);
      this.sourceOptions = this.sourceOptions.concat(projectBoards);

      // 2. Fetch Buckets
      const buckets = await this.bucketService.getAllByBoard(
        this.data.board.boardID
      );
      this.boardBuckets = this.boardBuckets.concat(buckets);

      // 3. Add buckets SECOND
      this.sourceOptions = this.sourceOptions.concat(buckets);
      this.destOptions = this.destOptions.concat(buckets);
    } catch (error) {
      console.error('Error loading boards and buckets:', error);
    }
  }

  // Fetches workflows for the board from the workflowService.
  async loadWorkflows(): Promise<void> {
    return this.workflowService.getAll(this.board.boardID).then((workflows) => {
      this.workflows = [];
      workflows.forEach((workflow) => {
        this.workflows.push(workflow);
      });
    });
  }

  // Creates a new bucket and updates UI.
  createBucket(): void {
    const bucket: Bucket = {
      bucketID: generateUniqueID(),
      boardID: this.data.board.boardID,
      name: this.bucketName,
      posts: [],
      addedToView: false,
    };

    this.bucketService.create(bucket).then(() => {
      this.loadBucketsBoards();
      this.openSnackBar('Bucket: ' + bucket.name + ' created succesfully!');
      this.bucketNameFormControl.reset();
      if (this.data?.onBucketCreation) {
        this.data?.onBucketCreation(bucket);
      }
    });
  }

  // Toggles visibility of bucket deletion controls.
  toggleDeleteBoard() {
    this.showDelete = !this.showDelete;
  }

  // Opens a confirmation dialog and deletes the bucket.
  deleteBucket(bucket: Bucket) {
    this.dialog.open(ConfirmModalComponent, {
      width: '500px',
      data: {
        title: 'Confirmation',
        message: 'Are you sure you want to delete this bucket?',
        handleConfirm: () => {
          this.bucketService.delete(bucket.bucketID).then(() => {
            this.loadBucketsBoards();
            this.openSnackBar(
              'Bucket: ' + bucket.name + ' deleted succesfully!'
            );
          });
        },
      },
    });
  }

  // Creates a distribution workflow.
  createDistributionWorkflow(): void {
    if (!this._distributionWorkflowTypeSelected()) return;

    const workflow: DistributionWorkflow = this._assembleDistributionWorkflow();
    this.workflowService.createDistribution(workflow).then(async () => {
      await this.loadWorkflows();
      this.selected.setValue(2);
      this._clearWorkflowForm();
    });
  }

  // Creates a peer review workflow.
  createPeerReviewWorkflow(): void {
    if (!this._actionSelected()) return;

    const workflow: TaskWorkflow = this._assemblePeerReviewWorkflow();
    this.workflowService.createTask(workflow).then(async () => {
      await this.loadWorkflows();
      this.selected.setValue(2);
      this._clearWorkflowForm();
    });
  }

  // Creates a generation task workflow.
  createGenerationTaskWorkflow(): void {
    if (!this._validGenerationTaskWorkflow()) return;
    const workflow: TaskWorkflow = this._assembleGenerationTaskWorkflow();
    this.workflowService.createTask(workflow).then(async () => {
      await this.loadWorkflows();
      this.selected.setValue(2);
      this._clearWorkflowForm();
    });
  }

  // Runs the specified workflow (task or distribution).
  runWorkflow(e, workflow: TaskWorkflow | DistributionWorkflow): void {
    e.stopPropagation();

    if (this._isTaskWorkflow(workflow)) {
      this.canvasService
        .runTaskWorkflow(workflow)
        .then(() => {
          workflow.active = true;
          this.openSnackBar('Workflow: ' + workflow.name + ' now active!');
        })
        .catch(() => {
          this.openSnackBar(
            'Unable to activate task workflow! Something went wrong.'
          );
        });
    } else {
      this.canvasService
        .runDistributionWorkflow(workflow)
        .then(async () => {
          this.openSnackBar(
            'Workflow: ' + workflow.name + ' completed successfully!'
          );
        })
        .catch(() => {
          this.openSnackBar('Cancelled workflow! Something went wrong.');
        });
    }
  }

  // Opens a confirmation dialog and deletes the workflow.
  async deleteWorkflow(
    e,
    workflow: TaskWorkflow | DistributionWorkflow
  ): Promise<void> {
    e.stopPropagation();

    this.dialog.open(ConfirmModalComponent, {
      width: '500px',
      data: {
        title: 'Confirmation',
        message: 'Are you sure you want to delete this workflow?',
        handleConfirm: async () => {
          if (this._isTaskWorkflow(workflow)) {
            await this.workflowService.removeTask(workflow.workflowID);
          } else {
            await this.workflowService.removeDistribution(workflow.workflowID);
          }

          this.workflows = this.workflows.filter(
            (w) => w.workflowID !== workflow.workflowID
          );
        },
      },
    });
  }

  // Closes the dialog.
  onNoClick(): void {
    this.dialogRef.close();
  }

  // Displays a snackbar message.
  openSnackBar(message: string): void {
    this.snackbarService.queueSnackbar(message);
  }

  // Resets the workflow creation form.
  _clearWorkflowForm() {
    this.workflowNameFormControl.reset();
    this.sourceFormControl.reset();
    this.destinationFormControl.reset();
    this.tagsFormControl.reset();
    this.workflowTypeFormControl.reset();
    this.removeFromSourceFormControl.reset();
    this.removeFromSource = false;
    this.postGeneration = 1;
  }

  // Type guard to check if an object is a Board.
  _isBoard(object: Board | Bucket): object is Board {
    return (object as Board).tags !== undefined;
  }

  // Type guard to check if an object is a TaskWorkflow.
  _isTaskWorkflow(
    object: DistributionWorkflow | TaskWorkflow
  ): object is TaskWorkflow {
    return (object as TaskWorkflow).requiredActions !== undefined;
  }

  // Checks if a distribution workflow form is valid.
  _validDistributionWorkflow(): boolean {
    const allowMatch = this.distributionDestinations.length > 1;
    const isMatch =
      this.distributionSource &&
      this.distributionDestinations.some(
        (dest) => dest.name === this.distributionSource.name
      );
    this.sourceDestinationMatchError.setValue(!allowMatch && isMatch);
    return (
      (allowMatch || !isMatch) &&
      this.workflowNameFormControl.valid &&
      this.sourceFormControl.valid &&
      this.destinationFormControl.valid
    );
  }

  _ppbSelected(): boolean {
    return this.postsPerBucket != null && this.postsPerBucket > 0;
  }

  _distributionWorkflowTypeSelected() {
    return (
      (this.distributionWorkflowType === DistributionWorkflowType.RANDOM &&
        this.postsPerBucket > 0) ||
      (this.distributionWorkflowType === DistributionWorkflowType.UPVOTES &&
        this.upvoteLimit) ||
      (this.distributionWorkflowType === DistributionWorkflowType.TAG &&
        this.selectedTag)
    );
  }

  _distributionWorkflowTypeData() {
    if (this.distributionWorkflowType === DistributionWorkflowType.RANDOM)
      return this.postsPerBucket;
    else if (this.distributionWorkflowType === DistributionWorkflowType.UPVOTES)
      return this.upvoteLimit;
    else return this.selectedTag;
  }

  _assembleDistributionWorkflow(): DistributionWorkflow {
    const workflowID: string = generateUniqueID();

    const workflow: DistributionWorkflow = {
      workflowID: workflowID,
      boardID: this.board.boardID,
      active: false,
      name: this.workflowName,
      source: this._mapToContainer(this.distributionSource),
      destinations: this._mapToContainers(this.distributionDestinations),
      distributionWorkflowType: {
        type: this.distributionWorkflowType,
        data: this._distributionWorkflowTypeData(),
      },
      removeFromSource: this.removeFromSource,
    };

    return workflow;
  }

  _validPeerReviewWorkflow(): boolean {
    return (
      this.workflowNameFormControl.valid &&
      this.sourceFormControl.valid &&
      this.destinationFormControl.valid &&
      this.groupsFormControl.valid &&
      this.promptFormControl.valid &&
      this._actionSelected()
    );
  }

  _validGenerationTaskWorkflow(): boolean {
    return (
      this.workflowNameFormControl.valid &&
      this.destinationFormControl.valid &&
      this.groupsFormControl.valid &&
      this.promptFormControl.valid
    );
  }

  _actionSelected(): boolean {
    return this.commentsRequired || this.tagsRequired;
  }

  _assemblePeerReviewWorkflow(): TaskWorkflow {
    const workflowID: string = generateUniqueID();

    const actions: TaskAction[] = [];
    if (this.commentsRequired)
      actions.push({
        type: TaskActionType.COMMENT,
        amountRequired: 1,
      });
    if (this.tagsRequired)
      actions.push({
        type: TaskActionType.TAG,
        amountRequired: 1,
      });

    const workflow: TaskWorkflow = {
      workflowID: workflowID,
      boardID: this.board.boardID,
      active: false,
      name: this.workflowName,
      source: this._mapToContainer(this.taskSource),
      destinations: [this._mapToContainer(this.taskDestination)],
      prompt: this.prompt,
      requiredActions: actions,
      assignedGroups: this.assignedGroups.map((g) => g.groupID),
      type: this.taskWorkflowType.PEER_REVIEW,
    };

    return workflow;
  }

  _assembleGenerationTaskWorkflow(): TaskWorkflow {
    const workflowID: string = generateUniqueID();

    const actions: TaskAction[] = [];
    if (this.postGeneration)
      actions.push({
        type: TaskActionType.CREATE_POST,
        amountRequired: this.postGeneration,
      });
    if (this.commentsRequired)
      actions.push({
        type: TaskActionType.COMMENT,
        amountRequired: 1,
      });
    if (this.tagsRequired)
      actions.push({
        type: TaskActionType.TAG,
        amountRequired: 1,
      });

    const workflow: TaskWorkflow = {
      workflowID: workflowID,
      boardID: this.board.boardID,
      active: false,
      name: this.workflowName,
      source: this._placeholderContainer(),
      destinations: [this._mapToContainer(this.taskDestination)],
      prompt: this.prompt,
      requiredActions: actions,
      assignedGroups: this.assignedGroups.map((g) => g.groupID),
      type: this.taskWorkflowType.GENERATION,
    };

    return workflow;
  }

  _validBucketForm(): boolean {
    return this.bucketNameFormControl.valid;
  }

  _forbiddenNameValidator(): ValidatorFn {
    return (control: AbstractControl): ValidationErrors | null => {
      const forbidden = this.board ? control.value == this.board.name : false;
      return forbidden ? { forbidden: { value: control.value } } : null;
    };
  }

  _mapToContainers(bucketsBoards: (Bucket | Board)[]): Container[] {
    return bucketsBoards.map((bOrB) => this._mapToContainer(bOrB));
  }

  _mapToContainer(bucketBoard: Bucket | Board): Container {
    if (this._isBoard(bucketBoard)) {
      return {
        type: ContainerType.BOARD,
        id: bucketBoard.boardID,
        name: bucketBoard.name,
      };
    } else {
      return {
        type: ContainerType.BUCKET,
        id: bucketBoard.bucketID,
        name: bucketBoard.name,
      };
    }
  }

  // Default to current board if no source required
  _placeholderContainer() {
    return {
      type: ContainerType.WORKFLOW,
      id: this.board.boardID,
      name: 'CK Workspace',
    };
  }
}
<h1 mat-dialog-title>CK Workflows</h1>
<div mat-dialog-content>
  <mat-tab-group
    dynamicHeight
    animationDuration="0ms"
    [selectedIndex]="selected.value"
    (selectedIndexChange)="selected.setValue($event)"
  >
    <mat-tab label="Manage Buckets">
      <h2 style="margin-top: 20px">Create New Bucket</h2>
      <div style="display: flex">
        <mat-form-field appearance="outline" style="width: 100%">
          <mat-label>Bucket Name</mat-label>
          <input
            matInput
            [formControl]="bucketNameFormControl"
            [errorStateMatcher]="matcher"
            [(ngModel)]="bucketName"
          />
          <mat-error *ngIf="bucketNameFormControl.hasError('required')"
            >Bucket name required!</mat-error
          >
          <mat-error *ngIf="bucketNameFormControl.hasError('forbidden')"
            >Bucket name cannot be the same as board name!</mat-error
          >
        </mat-form-field>
        <button
          mat-button
          *ngIf="selected.value === 0"
          [disabled]="!_validBucketForm()"
          (click)="createBucket()"
        >
          Create Bucket!
        </button>
      </div>

      <div>
        <button class="delete-toggler" mat-button (click)="toggleDeleteBoard()">
          Delete Buckets ({{ boardBuckets.length }})
          <mat-icon>{{ showDelete ? 'expand_less' : 'expand_more' }}</mat-icon>
        </button>
      </div>
      <div
        style="display: grid; grid-template-columns: 1fr 1fr"
        *ngIf="showDelete"
      >
        <mat-card
          *ngFor="let bucket of boardBuckets"
          style="
            display: flex;
            margin: 10px;
            padding: 10px;
            justify-content: space-between;
          "
        >
          <mat-card-title style="font-size: small; margin: auto 0">
            {{ bucket.name }}
          </mat-card-title>
          <button mat-icon-button (click)="deleteBucket(bucket)">
            <mat-icon style="color: red"> delete </mat-icon>
          </button>
        </mat-card>
      </div>
    </mat-tab>
    <mat-tab label="Create Workflow">
      <mat-radio-group [(ngModel)]="workflowType">
        <mat-radio-button
          style="margin-top: 20px"
          [checked]="true"
          [value]="WorkflowType.GENERATION"
          >Generation Task</mat-radio-button
        >
        <mat-radio-button
          style="margin-top: 20px"
          [checked]="false"
          [value]="WorkflowType.TASK"
          >Peer Review</mat-radio-button
        >
        <mat-radio-button
          style="margin-top: 20px"
          [checked]="false"
          [value]="WorkflowType.DISTRIBUTION"
          >Distribution</mat-radio-button
        >
      </mat-radio-group>
      <div *ngIf="workflowType === WorkflowType.DISTRIBUTION">
        <mat-form-field appearance="fill" style="margin-top: 10px">
          <mat-label>Workflow Name</mat-label>
          <input
            [formControl]="workflowNameFormControl"
            [errorStateMatcher]="matcher"
            matInput
            [(ngModel)]="workflowName"
          />
          <mat-error *ngIf="workflowNameFormControl.hasError('required')"
            >Workflow name required!</mat-error
          >
        </mat-form-field>
        <div class="source-destination">
          <div>
            <mat-form-field
              appearance="fill"
              style="font-size: 14px !important"
            >
              <mat-label>Source</mat-label>
              <mat-select
                [(ngModel)]="distributionSource"
                [formControl]="sourceFormControl"
                (ngModelChange)="_validDistributionWorkflow()"
              >
                <mat-option
                  *ngFor="let option of sourceOptions"
                  [value]="option"
                >
                  <ng-container *ngIf="option.hasOwnProperty('projectID')"
                    >CANVAS:</ng-container
                  >
                  <ng-container *ngIf="!option.hasOwnProperty('projectID')"
                    >BUCKET:</ng-container
                  >
                  {{ option.name }}</mat-option
                >
              </mat-select>
              <mat-error *ngIf="sourceFormControl.hasError('required')"
                >Source required!</mat-error
              >
            </mat-form-field>
            <mat-error *ngIf="sourceDestinationMatchError.value">
              Source and destination cannot be the same!
            </mat-error>
          </div>
          <mat-icon class="arrow-icon-lg">arrow_right_alt</mat-icon>
          <mat-form-field appearance="fill" style="font-size: 14px !important">
            <mat-label>Destination</mat-label>
            <mat-select
              [(ngModel)]="distributionDestinations"
              [formControl]="destinationFormControl"
              (ngModelChange)="_validDistributionWorkflow()"
              multiple
            >
              <mat-option *ngFor="let option of destOptions" [value]="option">
                <ng-container *ngIf="option.hasOwnProperty('projectID')"
                  >CANVAS:</ng-container
                >
                <ng-container *ngIf="!option.hasOwnProperty('projectID')"
                  >BUCKET:</ng-container
                >
                {{ option.name }}
              </mat-option>
            </mat-select>
            <mat-error *ngIf="destinationFormControl.hasError('required')"
              >Destination required!</mat-error
            >
          </mat-form-field>
        </div>
        <section></section>
        <section>
          <h4>Choose Distribution:</h4>
          <mat-radio-group
            [(ngModel)]="distributionWorkflowType"
            [formControl]="workflowTypeFormControl"
          >
            <mat-radio-button value="RANDOM"
              >Randomly distribute</mat-radio-button
            >
            <mat-radio-button value="TAG"
              >Copy all with specified tag</mat-radio-button
            >
            <mat-radio-button value="UPVOTES"
              >Copy all with min # upvotes</mat-radio-button
            >
          </mat-radio-group>
          <div style="margin: 20px 0">
            <section *ngIf="distributionWorkflowType === 'RANDOM'">
              <mat-form-field class="example-full-width" appearance="fill">
                <mat-label>Posts per Destination</mat-label>
                <input
                  type="number"
                  min="0"
                  [(ngModel)]="postsPerBucket"
                  matInput
                />
              </mat-form-field>
              <mat-error *ngIf="postsPerBucket <= 0">
                Enter a Positive Number
              </mat-error>
            </section>
            <section *ngIf="distributionWorkflowType === 'TAG'">
              <mat-form-field appearance="fill">
                <mat-label>Tags</mat-label>
                <mat-select [(ngModel)]="selectedTag">
                  <mat-option *ngFor="let tag of tags" [value]="tag">
                    {{ tag.name }}
                  </mat-option>
                </mat-select>
              </mat-form-field>
            </section>
            <section *ngIf="distributionWorkflowType === 'UPVOTES'">
              <div>
                <mat-label style="font-weight: bold"
                  >Minimum Upvotes Per Post: {{ upvoteLimit }}</mat-label
                >
                <mat-slider
                  class="slider"
                  min="1"
                  max="{{ board.upvoteLimit }}"
                  step="1"
                  [(ngModel)]="upvoteLimit"
                  thumbLabel
                ></mat-slider>
              </div>
            </section>
          </div>
        </section>
        <p>
          <mat-checkbox
            [(ngModel)]="removeFromSource"
            [formControl]="removeFromSourceFormControl"
          >
            Remove From Source
          </mat-checkbox>
        </p>
      </div>
      <div *ngIf="workflowType === WorkflowType.TASK">
        <mat-form-field appearance="fill" style="margin-top: 10px">
          <mat-label>Workflow Name</mat-label>
          <input
            [formControl]="workflowNameFormControl"
            [errorStateMatcher]="matcher"
            matInput
            [(ngModel)]="workflowName"
          />
          <mat-error *ngIf="workflowNameFormControl.hasError('required')"
            >Workflow name required!</mat-error
          >
        </mat-form-field>
        <div class="source-destination">
          <mat-form-field appearance="fill" style="font-size: 14px !important">
            <mat-label>Source</mat-label>
            <mat-select
              [(ngModel)]="taskSource"
              [formControl]="sourceFormControl"
              [errorStateMatcher]="matcher"
            >
              <mat-option *ngFor="let option of sourceOptions" [value]="option">
                <ng-container *ngIf="option.hasOwnProperty('projectID')"
                  >CANVAS:</ng-container
                >
                <ng-container *ngIf="!option.hasOwnProperty('projectID')"
                  >BUCKET:</ng-container
                >
                {{ option.name }}</mat-option
              >
            </mat-select>
            <mat-error *ngIf="sourceFormControl.hasError('required')"
              >Source required!</mat-error
            >
          </mat-form-field>
          <mat-icon class="arrow-icon-lg">arrow_right_alt</mat-icon>
          <mat-form-field appearance="fill" style="font-size: 14px !important">
            <mat-label>Destination</mat-label>
            <mat-select
              [(ngModel)]="taskDestination"
              [formControl]="destinationFormControl"
              [errorStateMatcher]="matcher"
            >
              <mat-option *ngFor="let option of destOptions" [value]="option">
                <ng-container *ngIf="option.hasOwnProperty('projectID')"
                  >CANVAS:</ng-container
                >
                <ng-container *ngIf="!option.hasOwnProperty('projectID')"
                  >BUCKET:</ng-container
                >
                {{ option.name }}</mat-option
              >
            </mat-select>
            <mat-error *ngIf="destinationFormControl.hasError('required')"
              >Destination required!</mat-error
            >
          </mat-form-field>
        </div>
        <section>
          <div style="display: flex; flex-direction: row; gap: 10px">
            <mat-form-field
              appearance="fill"
              style="font-size: 14px !important"
            >
              <mat-label>Assigned Groups</mat-label>
              <mat-select
                [(ngModel)]="assignedGroups"
                [formControl]="groupsFormControl"
                [errorStateMatcher]="matcher"
                multiple
              >
                <mat-option
                  *ngFor="let option of groupOptions"
                  [value]="option"
                  >{{ option.name }}</mat-option
                >
              </mat-select>
              <mat-error *ngIf="groupsFormControl.hasError('required')"
                >At least one group required!</mat-error
              >
            </mat-form-field>
          </div>
        </section>
        <section>
          <mat-form-field appearance="fill" style="width: 100%">
            <mat-label>Student Prompt</mat-label>
            <textarea
              matInput
              [formControl]="promptFormControl"
              [errorStateMatcher]="matcher"
              [(ngModel)]="prompt"
              placeholder="Please use tags to categorize each idea (e.g., financial, transportation, technology)"
            ></textarea>
            <mat-error *ngIf="promptFormControl.hasError('required')"
              >Prompt is required!</mat-error
            >
          </mat-form-field>
        </section>
        <section>
          <h4 style="font-weight: bold">Workflow Actions:</h4>
          <div style="display: flex; flex-direction: column; gap: 10px">
            <mat-checkbox [(ngModel)]="commentsRequired">
              Require minimum 1 comment per post
            </mat-checkbox>
            <mat-checkbox [(ngModel)]="tagsRequired">
              Require minimum 1 tag per post
            </mat-checkbox>
          </div>
        </section>
      </div>
      <div *ngIf="workflowType === WorkflowType.GENERATION">
        <mat-form-field appearance="fill" style="margin-top: 10px">
          <mat-label>Workflow Name</mat-label>
          <input
            [formControl]="workflowNameFormControl"
            [errorStateMatcher]="matcher"
            matInput
            [(ngModel)]="workflowName"
          />
          <mat-error *ngIf="workflowNameFormControl.hasError('required')"
            >Workflow name required!</mat-error
          >
        </mat-form-field>
        <div class="source-destination">
          <mat-form-field appearance="fill" style="font-size: 14px !important">
            <mat-label>Destination</mat-label>
            <mat-select
              [(ngModel)]="taskDestination"
              [formControl]="destinationFormControl"
              [errorStateMatcher]="matcher"
            >
              <mat-option *ngFor="let option of destOptions" [value]="option">
                <ng-container *ngIf="option.hasOwnProperty('projectID')"
                  >CANVAS:</ng-container
                >
                <ng-container *ngIf="!option.hasOwnProperty('projectID')"
                  >BUCKET:</ng-container
                >
                {{ option.name }}</mat-option
              >
            </mat-select>
            <mat-error *ngIf="destinationFormControl.hasError('required')"
              >Destination required!</mat-error
            >
          </mat-form-field>
        </div>
        <section>
          <div style="display: flex; flex-direction: row; gap: 10px">
            <mat-form-field
              appearance="fill"
              style="font-size: 14px !important"
            >
              <mat-label>Assigned Groups</mat-label>
              <mat-select
                [(ngModel)]="assignedGroups"
                [formControl]="groupsFormControl"
                [errorStateMatcher]="matcher"
                multiple
              >
                <mat-option
                  *ngFor="let option of groupOptions"
                  [value]="option"
                  >{{ option.name }}</mat-option
                >
              </mat-select>
              <mat-error *ngIf="groupsFormControl.hasError('required')"
                >At least one group required!</mat-error
              >
            </mat-form-field>
          </div>
        </section>
        <section>
          <mat-form-field appearance="fill" style="width: 100%">
            <mat-label>Student Prompt</mat-label>
            <textarea
              matInput
              [formControl]="promptFormControl"
              [errorStateMatcher]="matcher"
              [(ngModel)]="prompt"
              placeholder="Please use tags to categorize each idea (e.g., financial, transportation, technology)"
            ></textarea>
            <mat-error *ngIf="promptFormControl.hasError('required')"
              >Prompt is required!</mat-error
            >
          </mat-form-field>
        </section>
        <section>
          <h4 style="font-weight: bold">Workflow Actions:</h4>
          <div>
            <mat-label>Number of Posts to generate:</mat-label>
            <mat-slider
              class="slider"
              min="1"
              max="10"
              step="1"
              thumbLabel
              [(ngModel)]="postGeneration"
            ></mat-slider>
          </div>
          <div style="display: flex; flex-direction: column; gap: 10px">
            <mat-checkbox [(ngModel)]="tagsRequired">
              Require minimum 1 tag per post
            </mat-checkbox>
          </div>
        </section>
      </div>
    </mat-tab>
    <mat-tab label="Manage Workflows">
      <h3
        *ngIf="!workflows || workflows.length === 0"
        style="color: grey; text-align: center"
      >
        No workflows created!
      </h3>
      <mat-accordion
        class="list-accordion"
        multi
        *ngFor="let workflow of workflows"
      >
        <mat-expansion-panel>
          <mat-expansion-panel-header>
            <mat-panel-title>
              <mat-icon
                [ngStyle]="{ color: workflow.active ? 'green' : 'grey' }"
                style="padding-right: 30px"
                >circle</mat-icon
              >
              {{ workflow.name }}
            </mat-panel-title>
            <mat-panel-description>
              <mat-chip-list>
                <mat-chip
                  color="primary"
                  selected
                  *ngIf="workflow.distributionWorkflowType"
                  >Distribution</mat-chip
                >
                <mat-chip
                  color="primary"
                  selected
                  *ngIf="
                    workflow.requiredActions &&
                    workflow.type != taskWorkflowType.GENERATION
                  "
                  >Peer Review</mat-chip
                >
                <mat-chip
                  color="primary"
                  selected
                  *ngIf="workflow.type == taskWorkflowType.GENERATION"
                  >Generation</mat-chip
                >
              </mat-chip-list>
              <div>
                <button
                  mat-icon-button
                  [disabled]="workflow.active"
                  (click)="runWorkflow($event, workflow)"
                >
                  <mat-icon id="workflow-action-run"
                    >play_circle_filled</mat-icon
                  >
                </button>
                <button
                  mat-icon-button
                  (click)="deleteWorkflow($event, workflow)"
                >
                  <mat-icon id="workflow-action-delete">delete</mat-icon>
                </button>
              </div>
            </mat-panel-description>
          </mat-expansion-panel-header>
          <div class="source-dest-info">
            <mat-chip-list>
              <mat-chip>{{ workflow.source.name }}</mat-chip>
            </mat-chip-list>
            <mat-icon class="arrow-icon-md">arrow_right_alt</mat-icon>
            <mat-chip-list>
              <mat-chip *ngFor="let destination of workflow.destinations">{{
                destination.name
              }}</mat-chip>
            </mat-chip-list>
          </div>
          <div *ngIf="workflow.distributionWorkflowType">
            <div *ngIf="workflow.distributionWorkflowType.type === 'RANDOM'">
              <h4>
                Distribution Type:
                <mat-chip color="accent" selected style="margin: 0 5px">
                  Random
                </mat-chip>
              </h4>
              <h4>
                Posts Per Bucket/Board:
                <mat-chip color="accent" selected style="margin: 0 5px">
                  {{ workflow.distributionWorkflowType.data }}
                </mat-chip>
              </h4>
            </div>
            <div *ngIf="workflow.distributionWorkflowType.type === 'TAG'">
              <h4>
                Distribution Type:
                <mat-chip color="accent" selected style="margin: 0 5px">
                  Tag
                </mat-chip>
              </h4>
              <h4>
                Selected Tag:<mat-chip
                  [ngStyle]="{
                    'background-color':
                      workflow.distributionWorkflowType.data.color
                  }"
                  style="margin: 0 5px"
                  selected
                  >{{ workflow.distributionWorkflowType.data.name }}
                </mat-chip>
              </h4>
            </div>
            <div *ngIf="workflow.distributionWorkflowType.type === 'UPVOTES'">
              <h4>
                Distribution Type:
                <mat-chip color="accent" selected style="margin: 0 5px">
                  Upvote
                </mat-chip>
              </h4>
              <h4>
                Minimum upvotes per post:
                <mat-chip color="accent" selected style="margin: 0 5px">
                  {{ workflow.distributionWorkflowType.data }}
                </mat-chip>
              </h4>
            </div>
          </div>
          <div *ngIf="workflow.requiredActions">
            <h4>
              Required Actions:
              <mat-chip
                color="accent"
                selected
                style="margin: 0 5px"
                *ngFor="let action of workflow.requiredActions"
              >
                1 {{ action.type.toLowerCase() }} per post
              </mat-chip>
            </h4>
          </div>
          <div *ngIf="workflow.type == taskWorkflowType.GENERATION">
            <h4>
              Required Actions:
              <mat-chip
                color="accent"
                selected
                style="margin: 0 5px"
                *ngFor="let action of workflow.requiredActions"
              >
                Generate {{ action.amountRequired.toString() }} post(s)
              </mat-chip>
            </h4>
          </div>
          <div *ngIf="workflow.removeFromSource">
            <mat-chip color="warn" selected>
              Remove From Source Enabled
            </mat-chip>
          </div>
        </mat-expansion-panel>
      </mat-accordion>
    </mat-tab>
  </mat-tab-group>
</div>
<div mat-dialog-actions>
  <button mat-button (click)="onNoClick()">Close</button>
  <button
    mat-button
    *ngIf="selected.value === 0"
    [disabled]="!_validBucketForm()"
    (click)="createBucket()"
  >
    Create Bucket!
  </button>
  <button
    mat-button
    *ngIf="selected.value === 1 && workflowType === WorkflowType.DISTRIBUTION"
    [disabled]="!_validDistributionWorkflow()"
    (click)="createDistributionWorkflow()"
  >
    Create Distribution Workflow!
  </button>
  <button
    mat-button
    *ngIf="selected.value === 1 && workflowType === WorkflowType.TASK"
    [disabled]="!_validPeerReviewWorkflow()"
    (click)="createPeerReviewWorkflow()"
  >
    Create Peer Review Workflow!
  </button>
  <button
    mat-button
    *ngIf="selected.value === 1 && workflowType === WorkflowType.GENERATION"
    [disabled]="!_validGenerationTaskWorkflow()"
    (click)="createGenerationTaskWorkflow()"
  >
    Create Generation Task Workflow!
  </button>
</div>
