import { Component, Inject } from '@angular/core';
import {
  MAT_LEGACY_DIALOG_DATA as MAT_DIALOG_DATA,
  MatLegacyDialog as MatDialog,
  MatLegacyDialogRef as MatDialogRef,
} from '@angular/material/legacy-dialog';
import { Router } from '@angular/router';
import {
  Board,
  BoardPermissions,
  BoardScope,
  ViewSettings,
  ViewType,
} from 'src/app/models/board';
import { Project } from 'src/app/models/project';
import { Tag } from 'src/app/models/tag';
import { BoardService } from 'src/app/services/board.service';
import { CanvasService } from 'src/app/services/canvas.service';
// import { FileUploadService } from 'src/app/services/fileUpload.service';
import { SocketService } from 'src/app/services/socket.service';
import { UpvotesService } from 'src/app/services/upvotes.service';
import { UserService } from 'src/app/services/user.service';
import { ImageSettings } from 'src/app/utils/FabricUtils';
import { generateUniqueID } from 'src/app/utils/Utils';
import {
  POST_TAGGED_BORDER_THICKNESS,
  SocketEvent,
  TAG_DEFAULT_COLOR,
} from 'src/app/utils/constants';
import { PostService } from '../../services/post.service';
import { ConfirmModalComponent } from '../confirm-modal/confirm-modal.component';

@Component({
  selector: 'app-configuration-modal',
  templateUrl: './configuration-modal.component.html',
  styleUrls: ['./configuration-modal.component.scss'],
})
export class ConfigurationModalComponent {
  readonly tagDefaultColor = TAG_DEFAULT_COLOR;

  project: Project;
  board: Board;

  boardID: string;
  boardName: string;
  questionAuthoringType: string;

  defaultView: ViewType = ViewType.BUCKETS;
  viewSettings: ViewSettings;

  isTeacherPersonalBoard = false;

  currentBgImage: any;
  newCompressedImage: any;

  taskTitle: string;
  taskMessage: string;

  permissions: BoardPermissions;

  tags: Tag[];
  newTagText = '';
  newTagColor: any = TAG_DEFAULT_COLOR;
  _POST_TAGGED_BORDER_THICKNESS = POST_TAGGED_BORDER_THICKNESS;

  initialZoom = 100;
  upvoteLimit = 5;

  BoardScope: typeof BoardScope = BoardScope;

  bgImgSettings: ImageSettings;
  backgroundPosX;
  backgroundPosY;
  backgroundScale;
  tagsChanged = false;

  members: string[] = [];

  constructor(
    public dialogRef: MatDialogRef<ConfigurationModalComponent>,
    public dialog: MatDialog,
    public postService: PostService,
    public boardService: BoardService,
    public userService: UserService,
    public upvoteService: UpvotesService,
    public canvasService: CanvasService,
//    public fileUploadService: FileUploadService,
    public socketService: SocketService,
    private router: Router,
    @Inject(MAT_DIALOG_DATA) public data: any
  ) {
    this.project = data.project;
    this.board = data.board;
    this.boardID = data.board.boardID;
    this.boardName = data.board.name;
    this.currentBgImage = data.board.bgImage;
    this.taskTitle = data.board.task?.title;
    this.taskMessage = data.board.task?.message;
    this.tags = data.board.tags ?? [];
    this.permissions = data.board.permissions;
    this.initialZoom = data.board.initialZoom;
    this.bgImgSettings = data.board.bgImage?.imgSettings;
    this.backgroundPosX = this.bgImgSettings?.left;
    this.backgroundPosY = this.bgImgSettings?.top;
    this.backgroundScale = this.bgImgSettings
      ? Math.round(this.bgImgSettings.scaleX * 100)
      : 100;
    this.upvoteLimit = data.board.upvoteLimit;
    this.isTeacherPersonalBoard = this.project.teacherIDs.includes(
      this.board.ownerID
    );
    if (data.board.defaultView) {
      this.defaultView = data.board.defaultView;
      this.viewSettings = data.board.viewSettings;
    }
  }

  async addTag() {
    this.tagsChanged = true;
    this.tags.push({
      tagID: generateUniqueID(),
      boardID: this.boardID,
      name: this.newTagText,
      color: this.newTagColor,
    });
    await this.boardService.update(this.boardID, { tags: this.tags });
    this.newTagText = '';
  }

  async removeTag(tagRemove) {
    this.tagsChanged = true;
    this.tags = this.tags.filter((tag) => tag != tagRemove);
    await this.boardService.update(this.boardID, { tags: this.tags });
  }

  compressFile() {
    // this.fileUploadService.compressFile().then(async (compressedImage) => {
    //   this.newCompressedImage = compressedImage;

    //   const board = await this.canvasService.updateBoardImage(
    //     this.boardID,
    //     this.newCompressedImage
    //   );
    //   this.data.update(board);
    //   this.currentBgImage = board.bgImage;
    //   if (board.bgImage) {
    //     this.bgImgSettings = board.bgImage.imgSettings;
    //     this.backgroundPosX = board.bgImage.imgSettings.left;
    //     this.backgroundPosY = board.bgImage.imgSettings.top;
    //     this.backgroundScale = board.bgImage
    //       ? Math.round(board.bgImage.imgSettings.scaleX * 100)
    //       : 100;
    //   }
    // });
  }

  async updateBoardImageSettings(): Promise<Board> {
    this.bgImgSettings.top = this.backgroundPosY;
    this.bgImgSettings.left = this.backgroundPosX;
    this.bgImgSettings.scaleX = this.backgroundScale / 100;
    this.bgImgSettings.scaleY = this.backgroundScale / 100;

    const board: Board = await this.canvasService.updateBoardImageSettings(
      this.boardID,
      this.bgImgSettings
    );
    return board;
  }

  async removeImage() {
    this.currentBgImage = null;
    const board = await this.canvasService.updateBoardImage(this.boardID, null);
    this.data.update(board);
  }

  async handleDialogSubmit() {
    let board: Board;
    board = await this.canvasService.updateBoardName(
      this.boardID,
      this.boardName.trim()
    );
    board = await this.canvasService.updateBoardTask(
      this.boardID,
      this.taskTitle,
      this.taskMessage
    );
    board = await this.canvasService.updateBoardPermissions(
      this.boardID,
      this.permissions
    );
    board = await this.canvasService.updateBoardUpvotes(
      this.boardID,
      this.upvoteLimit
    );

    if (this.currentBgImage) board = await this.updateBoardImageSettings();

    board = await this.boardService.update(this.boardID, {
      initialZoom: this.initialZoom,
      defaultView: this.defaultView,
      viewSettings: this.viewSettings,
    });
    await this.data.update(board);
    this.dialogRef.close();
  }

  async ngOnDestroy(): Promise<void> {
    if (this.tagsChanged) {
      this.socketService.emit(
        SocketEvent.BOARD_TAGS_UPDATE,
        await this.boardService.get(this.boardID)
      );
    }
  }

  async handleClearBoard() {
    this.dialog.open(ConfirmModalComponent, {
      width: '500px',
      data: {
        title: 'Confirmation',
        message:
          'Are you sure you want to clear posts from this board? NOTE: Posts will be cleared from the board but remain in the the list view and any assigned buckets.',
        handleConfirm: async () => {
          this.postService.getAllByBoard(this.boardID).then(async (data) => {
            await this.canvasService.clearPostsFromBoard(data);
          });
        },
      },
    });
  }

  async handleDeleteBoard() {
    this.dialog.open(ConfirmModalComponent, {
      width: '500px',
      data: {
        title: 'Confirmation',
        message:
          'This will permanently delete the board and all related content. Are you sure you want to do this?',
        handleConfirm: async () => {
          const board = await this.boardService.remove(this.boardID);
          if (board) {
            this.dialogRef.close();
            this.router.navigate(['project/' + this.project.projectID]);
            await this.data.update(board, true);
          }
        },
      },
    });
  }

  resetColor() {
    this.newTagColor = TAG_DEFAULT_COLOR;
  }

  onNoClick(): void {
    this.dialogRef.close();
  }

  openVoteDeleteDialog() {
    this.dialog.open(ConfirmModalComponent, {
      width: '500px',
      data: {
        title: 'Confirmation',
        message: 'Are you sure you want to clear all votes from the board?',
        handleConfirm: async () => {
          await this.upvoteService.removeByBoard(this.boardID);
        },
      },
    });
  }

  async copyConfiguration() {
    let boards = await this.boardService.getMultipleBy(this.project.boards, {
      scope: BoardScope.PROJECT_PERSONAL,
    });
    boards = boards.filter((b) => {
      if (this.isTeacherPersonalBoard) {
        return this.project.teacherIDs.includes(b.ownerID);
      } else {
        return !this.project.teacherIDs.includes(b.ownerID);
      }
    });

    await this.boardService.copyConfiguration(
      this.boardID,
      boards.map((b) => b.boardID)
    );
  }
}
<h1 mat-dialog-title>Board Configuration</h1>
<div mat-dialog-content>
  <mat-tab-group dynamicHeight animationDuration="0ms">
    <mat-tab label="Board">
      <div class="board-name-form">
        <mat-form-field appearance="outline" style="width: 100%">
          <mat-label>Board Name</mat-label>
          <input
            matInput
            [(ngModel)]="boardName"
            #boardNameInput="ngModel"
            required
          />
          <mat-error *ngIf="boardNameInput.invalid">
            Board Name is required.
          </mat-error>
        </mat-form-field>
      </div>
      <p>
        <mat-checkbox [(ngModel)]="permissions.allowTracing">
          Enable logging of all user activities
        </mat-checkbox>
      </p>
      <p>
        <mat-checkbox [(ngModel)]="permissions.showSnackBarStudent"
          >Display task description when board is opened
        </mat-checkbox>
      </p>
      <h2 class="section-title">Voting</h2>
      <div>
        <mat-label style="font-weight: bold"
          >Maximum Upvotes Per Student: {{ upvoteLimit }}</mat-label
        >
        <mat-slider
          class="slider"
          min="1"
          max="20"
          step="1"
          [(ngModel)]="upvoteLimit"
          thumbLabel
        ></mat-slider>
        <mat-chip
          class="config-button"
          mat-raised-button
          color="warn"
          (click)="openVoteDeleteDialog()"
          style="text-align: center"
          selected
        >
          <mat-icon class="chip-icon">clear_all</mat-icon>
          <span style="width: 100%; text-align: center"> Clear Votes </span>
        </mat-chip>
      </div>

      <ng-container *ngIf="!!viewSettings">
        <br />
        <p>
          <label><strong>Default View</strong></label>
          <mat-radio-group [(ngModel)]="defaultView">
            <mat-radio-button style="margin-left: 10px;" value="CANVAS"
              [disabled]="viewSettings.allowCanvas === false">Canvas</mat-radio-button>
            <mat-radio-button style="margin-left: 10px;" value="WORKSPACE"
              [disabled]="viewSettings.allowWorkspace === false">Workspace</mat-radio-button>
            <mat-radio-button style="margin-left: 10px;" value="BUCKETS"
              [disabled]="viewSettings.allowBuckets === false">Buckets</mat-radio-button>
            <mat-radio-button style="margin-left: 10px;" value="MONITOR"
              [disabled]="viewSettings.allowMonitor === false">Monitor</mat-radio-button>
          </mat-radio-group>
        </p>
        <p>
          <label><strong>Enable/Disable Views</strong></label>
          <mat-checkbox style="margin-left: 10px;" [(ngModel)]="viewSettings.allowCanvas"
            [disabled]="defaultView === 'CANVAS'">Canvas</mat-checkbox>
          <mat-checkbox style="margin-left: 10px;" [(ngModel)]="viewSettings.allowWorkspace"
            [disabled]="defaultView === 'WORKSPACE'">Workspace</mat-checkbox>
          <mat-checkbox style="margin-left: 10px;" [(ngModel)]="viewSettings.allowBuckets"
            [disabled]="defaultView === 'BUCKETS'">Buckets</mat-checkbox>
          <mat-checkbox style="margin-left: 10px;" [(ngModel)]="viewSettings.allowMonitor"
            [disabled]="defaultView === 'MONITOR'">Monitor</mat-checkbox>
        </p>
      </ng-container>

      <h2 class="section-title">Board</h2>
      <div>
        <mat-label style="font-weight: bold"
          >Initial Zoom: {{ initialZoom }}%</mat-label
        >
        <mat-slider
          class="slider"
          min="0"
          max="200"
          step="5"
          [(ngModel)]="initialZoom"
          thumbLabel
        ></mat-slider>
      </div>
      <div class="image-form">
        <div class="container">
          <mat-chip
            class="chip"
            color="primary"
            selected
            (click)="compressFile()"
          >
            <mat-icon class="chip-icon" (click)="removeImage()"
              >upload</mat-icon
            >
            <span>Upload New Background Image</span>
          </mat-chip>
          <mat-chip
            class="chip"
            *ngIf="currentBgImage && currentBgImage.url"
            color="warn"
            selected
            (click)="removeImage()"
          >
            <mat-icon class="chip-icon">cancel</mat-icon>
            <span>Remove Current Background Image</span>
          </mat-chip>
        </div>
        <div *ngIf="currentBgImage">
          <div style="margin-top: 15px">
            <mat-label style="font-weight: bold"
              >Background Size: {{ backgroundScale }}%</mat-label
            >
            <mat-slider
              class="slider"
              min="0"
              max="1000"
              step="5"
              [(ngModel)]="backgroundScale"
              thumbLabel
            ></mat-slider>
          </div>
          <div class="bg-position">
            <mat-label style="font-weight: bold">Background Position</mat-label>
            <div class="bg-position-form">
              <mat-form-field>
                <mat-label>Background Pos X</mat-label>
                <input matInput type="number" [(ngModel)]="backgroundPosX" />
              </mat-form-field>
              <mat-form-field>
                <mat-label>Background Pos Y</mat-label>
                <input matInput type="number" [(ngModel)]="backgroundPosY" />
              </mat-form-field>
            </div>
          </div>
        </div>
      </div>
      <h2 class="section-title">Content Settings</h2>
      <div style="display: grid; grid-template-columns: 1fr 1fr">
        <mat-chip
          class="config-button"
          *ngIf="board.scope === BoardScope.PROJECT_PERSONAL"
          mat-raised-button
          color="accent"
          (click)="copyConfiguration()"
          style="text-align: center"
          selected
        >
          <mat-icon class="chip-icon" style="width: 0px; overflow: initial"
            >file_copy</mat-icon
          >
          <span style="width: 100%; text-align: center; font-size: smaller">
            Copy Configuration to all Personal
            {{ isTeacherPersonalBoard ? 'Teacher' : 'Student' }} Boards
          </span>
        </mat-chip>
        <mat-chip
          class="config-button"
          mat-raised-button
          color="warn"
          (click)="handleClearBoard()"
          style="text-align: center"
          selected
        >
          <mat-icon class="chip-icon" style="width: 0px; overflow: initial"
            >clear_all</mat-icon
          >
          <span style="width: 100%; text-align: center"> Clear Board </span>
        </mat-chip>
        <mat-chip
          class="config-button"
          mat-raised-button
          color="warn"
          (click)="handleDeleteBoard()"
          style="text-align: center"
          selected
        >
          <mat-icon class="chip-icon" style="width: 0px; overflow: initial"
            >delete</mat-icon
          >
          <span style="width: 100%; text-align: center"> Delete Board </span>
        </mat-chip>
      </div>
    </mat-tab>
    <mat-tab label="Task">
      <mat-form-field
        appearance="outline"
        style="width: 100%; margin-top: 20px"
      >
        <mat-label>Task Title</mat-label>
        <input matInput [(ngModel)]="taskTitle" #taskTitleInput="ngModel" />
        <mat-hint
          class="mat-error"
          *ngIf="
            (taskMessage && !taskTitle.trim()) ||
            (taskTitle && !taskTitle.trim())
          "
        >
          Task Title is required.
        </mat-hint>
      </mat-form-field>
      <mat-form-field appearance="outline" style="width: 100%">
        <mat-label>Task Message</mat-label>
        <textarea
          matInput
          rows="5"
          cols="40"
          [(ngModel)]="taskMessage"
        ></textarea>
      </mat-form-field>
    </mat-tab>
    <mat-tab label="Permissions">
      <h2 style="margin-top: 20px">Post Features</h2>
      <p>
        <mat-checkbox [(ngModel)]="permissions.allowStudentMoveAny"
          >Allow students to move any post</mat-checkbox
        >
      </p>
      <h2 style="margin-top: 20px">Post Features</h2>
      <p>
        <mat-checkbox [(ngModel)]="permissions.allowStudentEditAddDeletePost"
          >Allow students to create, edit, and delete posts</mat-checkbox
        >
      </p>
      <p>
        <mat-checkbox [(ngModel)]="permissions.allowStudentUpvoting"
          >Allow students to upvote posts</mat-checkbox
        >
      </p>
      <p>
        <mat-checkbox [(ngModel)]="permissions.allowStudentCommenting"
          >Allow students to comment on posts</mat-checkbox
        >
      </p>
      <p>
        <mat-checkbox [(ngModel)]="permissions.allowStudentTagging"
          >Allow students to tag posts</mat-checkbox
        >
      </p>
      <h2>Anonymization</h2>
      <p>
        <mat-checkbox [(ngModel)]="permissions.showAuthorNameStudent"
          >Show author names to students</mat-checkbox
        >
      </p>
      <p>
        <mat-checkbox [(ngModel)]="permissions.showAuthorNameTeacher"
          >Show author names to teachers</mat-checkbox
        >
      </p>
      <h2>UI Elements</h2>
      <p>
        <mat-checkbox [(ngModel)]="permissions.showBucketStudent"
          >Show buckets to students</mat-checkbox
        >
      </p>
    </mat-tab>
    <mat-tab label="Tags">
      <div class="tags-list">
        <mat-chip-list>
          <mat-chip
            *ngFor="let tag of tags"
            [ngStyle]="{ 'background-color': tag.color }"
          >
            {{ tag.name }}
            <mat-icon
              *ngIf="
                tag?.specialAttributes?.borderWidth !==
                _POST_TAGGED_BORDER_THICKNESS
              "
              class="cancel"
              (click)="removeTag(tag)"
              >cancel</mat-icon
            >
          </mat-chip>
        </mat-chip-list>
      </div>
      <mat-form-field appearance="fill">
        <mat-label>New Tag Name...</mat-label>
        <input matInput [(ngModel)]="newTagText" (keyup.enter)="addTag()" />
        <button mat-icon-button matSuffix>
          <mat-icon [(colorPicker)]="newTagColor" [style.color]="newTagColor">
            circle
          </mat-icon>
        </button>
      </mat-form-field>
      <button
        mat-button
        [disabled]="newTagText.length < 1"
        color="primary"
        (click)="addTag()"
      >
        Add Tag
      </button>
      <button
        mat-button
        [disabled]="newTagColor === tagDefaultColor"
        color="primary"
        (click)="resetColor()"
      >
        Reset Color
      </button>
    </mat-tab>
  </mat-tab-group>
</div>
<div mat-dialog-actions>
  <button mat-button (click)="onNoClick()">Cancel</button>
  <button
    mat-button
    (click)="handleDialogSubmit()"
    [disabled]="
      (taskMessage && !taskTitle.trim()) ||
      (taskTitle && !taskTitle.trim()) ||
      boardNameInput.invalid
    "
    cdkFocusInitial
  >
    Update Configuration!
  </button>
</div>
