import { Component, OnInit } from '@angular/core';
import { Board, BoardScope, ViewType } from 'src/app/models/board';
import { Project } from 'src/app/models/project';
import User, { AuthUser, Role } from 'src/app/models/user';
import { BoardService } from 'src/app/services/board.service';
import { ProjectService } from 'src/app/services/project.service';
import { Router } from '@angular/router';
import { MatDialog } from '@angular/material/dialog';
import { AddBoardModalComponent } from '../add-board-modal/add-board-modal.component';
import { ConfigurationModalComponent } from '../configuration-modal/configuration-modal.component';
import { ConfirmModalComponent } from '../confirm-modal/confirm-modal.component';
import { ProjectConfigurationModalComponent } from '../project-configuration-modal/project-configuration-modal.component';
import { TodoListModalComponent } from '../todo-list-modal/todo-list-modal.component';
import { UserService } from 'src/app/services/user.service';
import { ManageGroupModalComponent } from '../groups/manage-group-modal/manage-group-modal.component';
import { ProjectTodoListModalComponent } from '../project-todo-list-modal/project-todo-list-modal.component';
import { SocketService } from 'src/app/services/socket.service';

@Component({
  selector: 'app-project-dashboard',
  templateUrl: './project-dashboard.component.html',
  styleUrls: ['./project-dashboard.component.scss'],
})
export class ProjectDashboardComponent implements OnInit {
  showSharedBoards = true;
  showStudentPersonalBoards = false;
  showTeacherPersonalBoards = false;

  teacherPersonalBoards: Board[] = [];
  studentPersonalBoards: Board[] = [];
  sharedBoards: Board[] = [];

  project: Project;
  user: AuthUser;
  teachers: AuthUser[];
  projectID: string;
  yourProjects: Project[] = [];

  Role: typeof Role = Role;
  BoardScope: typeof BoardScope = BoardScope;

  constructor(
    public boardService: BoardService,
    public projectService: ProjectService,
    public userService: UserService,
    public socketService: SocketService,
    public dialog: MatDialog,
    private router: Router
  ) {}

  async ngOnInit(): Promise<void> {
    this.user = this.userService.user!;
    this.projectID = this.router.url.substring(
      this.router.url.lastIndexOf('/') + 1
    );
    await this.getBoards();
    await this.getUsersProjects(this.user.userID);
  }

  async getBoards() {
    this.project = await this.projectService.get(this.projectID);
    const boards = await this.boardService.getByProject(this.projectID);
    boards.forEach((board) => {
      if (board.scope == BoardScope.PROJECT_PERSONAL) {
        const isTeacher = this.project.teacherIDs.includes(board.ownerID);
        if (isTeacher) this.teacherPersonalBoards.push(board);
        else this.studentPersonalBoards.push(board);
      } else if (board.scope == BoardScope.PROJECT_SHARED) {
        this.sharedBoards.push(board);
      }
    });
  }

  async getUsersProjects(id) {
    const projects = await this.projectService.getByUserID(id);
    this.yourProjects = projects;
  }

  openCreateBoardDialog() {
    this.dialog.open(AddBoardModalComponent, {
      width: '700px',
      data: {
        user: this.user,
        createBoard: this.createBoard,
        projects: this.yourProjects,
        defaultProject: this.projectID,
      },
    });
  }

  createBoard = async (board: Board, selectedProjectID: string) => {
    const projectBoards = this.yourProjects.find(
      (project) => project.projectID == selectedProjectID
    )?.boards;

    if (projectBoards) {
      await this.boardService.create(board);
      await this.projectService.update(selectedProjectID, {
        boards: [...projectBoards, board.boardID],
      });

      const view = board.defaultView ? board.defaultView.toLowerCase() : '';
      this.router.navigate([
        `project/${this.projectID}/board/${board.boardID}/${view}`,
      ]);
    }
  };

  openSettingsDialog() {
    this.dialog
      .open(ProjectConfigurationModalComponent, {
        data: { project: this.project, user: this.user },
      })
      .afterClosed()
      .subscribe((p?: Project) => {
        if (p) this.project = p;
      });
  }

  openTodoList() {
    this.router.navigate([`/project/${this.projectID}/todo`]);
  }

  openProjectTodoList() {
    this.router.navigate([`/project/${this.projectID}/todo`]);
  }

  toggleBoardVisibility(event: any, board: Board) {
    event.stopPropagation();
    if (board.visible) this.socketService.disconnectAll(board.boardID);
    this.boardService.update(board.boardID, { visible: !board.visible });
    board.visible = !board.visible;
  }

  openGroupDialog() {
    this.dialog.open(ManageGroupModalComponent, {
      data: {
        project: this.project,
      },
    });
  }

  handleBoardClick(boardID, defaultView: ViewType | undefined | null) {
    const view = defaultView ? defaultView.toLowerCase() : 'buckets';
    this.router.navigate([
      `project/${this.projectID}/board/${boardID}/${view}`,
    ]);
  }

  async handleEditBoard(board: Board) {
    const boardID: string = board.boardID;
    this.dialog.open(ConfigurationModalComponent, {
      width: '700px',
      data: {
        project: this.project,
        board: await this.boardService.get(boardID),
        update: async (updatedBoard: Board, removed = false) => {
          if (removed) {
            this.sharedBoards = this.sharedBoards.filter(
              (b) => b.boardID !== updatedBoard.boardID
            );
            this.studentPersonalBoards = this.studentPersonalBoards.filter(
              (b) => b.boardID !== updatedBoard.boardID
            );
            this.teacherPersonalBoards = this.teacherPersonalBoards.filter(
              (b) => b.boardID !== updatedBoard.boardID
            );
          }
          if (updatedBoard.name !== board.name) {
            board.name = updatedBoard.name;
          }
        },
      },
    });
  }

  async handleDeleteBoard(board: Board) {
    this.dialog.open(ConfirmModalComponent, {
      width: '500px',
      data: {
        title: 'Confirmation',
        message:
          'This will permanently delete the board and all related content. Are you sure you want to do this?',
        handleConfirm: async () => {
          const deletedBoard = await this.boardService.remove(board.boardID);
          if (deletedBoard) {
            this.sharedBoards = this.sharedBoards.filter(
              (b) => b.boardID !== deletedBoard.boardID
            );
            this.studentPersonalBoards = this.studentPersonalBoards.filter(
              (b) => b.boardID !== deletedBoard.boardID
            );
            this.teacherPersonalBoards = this.teacherPersonalBoards.filter(
              (b) => b.boardID !== deletedBoard.boardID
            );
          }
        },
      },
    });
  }

  signOut(): void {
    this.userService.logout();
    this.router.navigate(['login']);
  }
}
<div class="app">
  <app-toolbar [user]="user">
    <span *ngIf="project">{{ project.name }}</span>
    <span style="flex: 1 1 auto"></span>
    <button
      mat-icon-button
      [matMenuTriggerFor]="addMenu"
      *ngIf="user && user.role === Role.TEACHER"
      matTooltip="Create Board"
    >
      <mat-icon>add</mat-icon>
    </button>

    <mat-menu #addMenu="matMenu">
      <button mat-menu-item (click)="openCreateBoardDialog()">
        Create Board
      </button>
    </mat-menu>

    <div *ngIf="project">
      <app-project-notification-dropdown
        [user]="user"
        [project]="project"
      ></app-project-notification-dropdown>
    </div>

    <button navbarMenu mat-icon-button [matMenuTriggerFor]="projectNavbarMenu">
      <mat-icon>dehaze</mat-icon>
    </button>

    <mat-menu #projectNavbarMenu="matMenu">
      <button
        mat-menu-item 
        *ngIf="user && user.role === Role.STUDENT"
        (click)="openTodoList()"
      >
        <mat-icon>assignment</mat-icon>
        Todo List
      </button>
      <app-csv-download-button
        [projectID]="this.projectID"
        *ngIf="user && user.role === Role.TEACHER"
      >
      </app-csv-download-button>
      <button
        mat-menu-item 
        *ngIf="user && user.role === Role.TEACHER"
        (click)="openProjectTodoList()"
      >
        <mat-icon>assignment_ind</mat-icon>
        View All Todo Lists
      </button>
      <button
        *ngIf="user && user.role === Role.TEACHER"
        mat-menu-item 
        (click)="openGroupDialog()"
      >
        <mat-icon>group_work</mat-icon>
        Manage Groups
      </button>

      <button
        *ngIf="user && user.role === Role.TEACHER"
        mat-menu-item
        (click)="openSettingsDialog()"
      >
        <mat-icon>settings</mat-icon>
        Adjust Settings
      </button>
      <a href="https://score.oise.utoronto.ca/" style="text-decoration:none;color:black" target="_blank" >
      <button mat-menu-item style="text-decoration:none;color:black">
          <mat-icon>launch</mat-icon>
          Go to SCORE
        </button>
      </a>
      <button mat-menu-item (click)="signOut()">
        <mat-icon class="material-icons-outlined">logout</mat-icon>
        Log Out
      </button>
    </mat-menu>
  </app-toolbar>
  <div class="content">
    <div class="heading">
      <button
        mat-icon-button
        routerLink="/dashboard"
        aria-label="Return to Dashboard"
      >
        <mat-icon>arrow_back</mat-icon>
      </button>
      <h3 class="mat-title" *ngIf="project">Boards for {{ project.name }}</h3>
    </div>
    <ng-container *ngIf="user.role === Role.TEACHER">
      <div>
        <button *ngIf="project && project.personalBoardSetting.enabled" class="subheader-button" mat-button (click)="showSharedBoards = !showSharedBoards">
          Shared Project Boards ({{ sharedBoards.length }})
          <mat-icon>{{ showSharedBoards ? 'expand_less' : 'expand_more' }}</mat-icon>
        </button>
        <div *ngIf="showSharedBoards" style="padding: 10px 0px">
          <div class="cards">
            <div *ngFor="let board of sharedBoards">
              <mat-card
                class="card"
                (click)="handleBoardClick(board.boardID, board.defaultView)"
                (keyup.enter)="handleBoardClick(board.boardID, board.defaultView)"
                role="button"
                tabindex="0"
                [attr.aria-label]="'View ' + board.name"
              >
                <mat-card-title class="title accent">
                  <mat-icon color="accent">picture_in_picture</mat-icon>
                  <span>{{ board.name }}</span>
                    <button 
                      *ngIf="user && user.role === Role.TEACHER"
                      mat-icon-button
                      [matMenuTriggerFor]="boardOptions"
                      style="margin:0; padding:0;line-height: inherit;height: inherit;right: 0;position: absolute;"
                      matTooltip="Board Options"
                      (click)="$event.stopPropagation();"
                    >
                      <mat-icon >
                        more_vert
                      </mat-icon>
                    </button>
                </mat-card-title>
                <button
                  mat-icon-button
                  class="accent"
                  *ngIf="user && user.role === Role.TEACHER"
                  matTooltip="Toggle Visibility"
                  (click)="toggleBoardVisibility($event, board)"
                >
                  <mat-icon *ngIf="board.visible">visibility</mat-icon>
                  <mat-icon *ngIf="!board.visible">visibility_off</mat-icon>
                </button>

                <mat-menu #boardOptions="matMenu">
                  <button mat-menu-item (click)="handleEditBoard(board)">
                    Edit
                  </button>
                  <button mat-menu-item (click)="handleDeleteBoard(board)">
                    Delete
                  </button>
                </mat-menu>
                <mat-card-content>
                  <mat-chip-list>
                    <mat-chip id="shared-chip" #tooltip="matTooltip" matTooltip="Visible to all project members.">
                      <mat-icon class="tag-icon">group</mat-icon>
                      Shared
                    </mat-chip>
                  </mat-chip-list>
                </mat-card-content>
              </mat-card>
            </div>
          </div>
        </div>
      </div>
      <div>
        <button *ngIf="project && project.personalBoardSetting.enabled" class="subheader-button" mat-button (click)="showStudentPersonalBoards = !showStudentPersonalBoards">
          Student Personal Boards ({{ studentPersonalBoards.length }})
          <mat-icon>{{ showStudentPersonalBoards ? 'expand_less' : 'expand_more' }}</mat-icon>
        </button>
        <div *ngIf="project && showStudentPersonalBoards && project.personalBoardSetting.enabled" style="padding: 10px 0px">
          <div class="cards">
            <ng-container *ngFor="let board of studentPersonalBoards">
              <mat-card
                class="card"
                (click)="handleBoardClick(board.boardID, board.defaultView)"
                (keyup.enter)="handleBoardClick(board.boardID, board.defaultView)"
                role="button"
                tabindex="0"
                [attr.aria-label]="'View ' + board.name"
              >
                <mat-card-title class="title accent">
                  <mat-icon color="accent">picture_in_picture</mat-icon>
                  <span>{{ board.name }}</span>
                </mat-card-title>
                <button
                  mat-icon-button
                  class="accent"
                  *ngIf="user && user.role === Role.TEACHER"
                  matTooltip="Toggle Visibility"
                  (click)="toggleBoardVisibility($event, board)"
                >
                  <mat-icon *ngIf="board.visible">visibility</mat-icon>
                  <mat-icon *ngIf="!board.visible">visibility_off</mat-icon>
                </button>
                <mat-card-content>
                  <mat-chip-list>
                    <mat-chip #tooltip="matTooltip" matTooltip="Only visible to teachers and assigned student.">
                      <mat-icon class="tag-icon">lock</mat-icon>
                      Personal
                    </mat-chip>
                  </mat-chip-list>
                </mat-card-content>
              </mat-card>
            </ng-container>
          </div>
        </div>
      </div>
      <div>
        <button *ngIf="project && project.personalBoardSetting.enabled" class="subheader-button" mat-button (click)="showTeacherPersonalBoards = !showTeacherPersonalBoards">
          Teacher Personal Boards ({{ teacherPersonalBoards.length }})
          <mat-icon>{{ showTeacherPersonalBoards ? 'expand_less' : 'expand_more' }}</mat-icon>
        </button>
        <div *ngIf="project && showTeacherPersonalBoards && project.personalBoardSetting.enabled" style="padding: 10px 0px">
          <div class="cards">
            <ng-container *ngFor="let board of teacherPersonalBoards">
              <mat-card
                class="card"
                (click)="handleBoardClick(board.boardID, board.defaultView)"
                (keyup.enter)="handleBoardClick(board.boardID, board.defaultView)"
                role="button"
                tabindex="0"
                [attr.aria-label]="'View ' + board.name"
              >
                <mat-card-title class="title accent">
                  <mat-icon color="accent">picture_in_picture</mat-icon>
                  <span>{{ board.name }}</span>
                </mat-card-title>
                <button
                  mat-icon-button
                  class="accent"
                  *ngIf="user && user.role === Role.TEACHER"
                  matTooltip="Toggle Visibility"
                  (click)="toggleBoardVisibility($event, board)"
                >
                  <mat-icon *ngIf="board.visible">visibility</mat-icon>
                  <mat-icon *ngIf="!board.visible">visibility_off</mat-icon>
                </button>
                <button 
                *ngIf="user && user.role === Role.TEACHER"
                mat-icon-button
                [matMenuTriggerFor]="boardOptions"
                style="margin:0; padding:0;line-height: inherit;height: inherit;right: 0;position: absolute;"
                matTooltip="Board Options"
                (click)="$event.stopPropagation();"
                >
                  <mat-icon >
                    more_vert
                  </mat-icon>
                </button>
                <mat-menu #boardOptions="matMenu">
                  <button mat-menu-item (click)="handleEditBoard(board)">
                    Edit
                  </button>
                  <button mat-menu-item (click)="handleDeleteBoard(board)">
                    Delete
                  </button>
                </mat-menu>
                <mat-card-content>
                  <mat-chip-list>
                    <mat-chip #tooltip="matTooltip" matTooltip="Only visible to teachers.">
                      <mat-icon class="tag-icon">lock</mat-icon>
                      Personal
                    </mat-chip>
                  </mat-chip-list>
                </mat-card-content>
              </mat-card>
            </ng-container>
          </div>
        </div>
      </div>
    </ng-container>

    <ng-container *ngIf="user.role === Role.STUDENT">
      <div class="cards">
        <ng-container *ngFor="let board of studentPersonalBoards.concat(sharedBoards)">
          <mat-card
            class="card"
            (click)="handleBoardClick(board.boardID, board.defaultView)"
            (keyup.enter)="handleBoardClick(board.boardID, board.defaultView)"
            role="button"
            tabindex="0"
            [attr.aria-label]="'View ' + board.name"
          >
            <mat-card-title class="title accent">
              <mat-icon color="accent">picture_in_picture</mat-icon>
              <span>{{ board.name }}</span>
            </mat-card-title>
            <mat-card-content>
              <mat-chip-list>
                <mat-chip *ngIf="board.scope === BoardScope.PROJECT_SHARED" id="shared-chip" #tooltip="matTooltip" matTooltip="Visible to all project members.">
                  <mat-icon class="tag-icon">group</mat-icon>
                  Shared
                </mat-chip>
                <mat-chip *ngIf="board.scope === BoardScope.PROJECT_PERSONAL" #tooltip="matTooltip" matTooltip="Only visible to you and project teachers.">
                  <mat-icon class="tag-icon">lock</mat-icon>
                  Personal
                </mat-chip>
              </mat-chip-list>
            </mat-card-content>
          </mat-card>
        </ng-container>
      </div>
    </ng-container>
  </div>
</div>
