import { Component, Inject, OnInit } from '@angular/core';
import { MatLegacyDialogRef as MatDialogRef, MAT_LEGACY_DIALOG_DATA as MAT_DIALOG_DATA } from '@angular/material/legacy-dialog';
import { Router } from '@angular/router';
import { ProjectService } from 'src/app/services/project.service';
import { UserService } from 'src/app/services/user.service';

@Component({
  selector: 'app-join-project-modal',
  templateUrl: './join-project-modal.component.html',
  styleUrls: ['./join-project-modal.component.scss'],
})
export class JoinProjectModalComponent implements OnInit {
  inputCode = '';
  isError = false;
  errorMessage = '';

  constructor(
    public dialogRef: MatDialogRef<JoinProjectModalComponent>,
    public projectService: ProjectService,
    public userService: UserService,
    public router: Router,
    @Inject(MAT_DIALOG_DATA) public data: any
  ) {}

  ngOnInit(): void {}

  joinProject() {
    this.projectService
      .joinProject(this.inputCode)
      .then((project) => {
        this.dialogRef.close();
        this.router.navigate(['project/' + project.projectID]);
      })
      .catch((e) => {
        this.showError(e.error);
      });
  }

  onNoClick(): void {
    this.dialogRef.close();
  }

  showError(message) {
    this.isError = true;
    this.errorMessage = message;
  }
}
<h1 mat-dialog-title>Join Project</h1>
<mat-form-field appearance="outline" style="width: 100%; margin-top: 20px">
  <mat-label>Invite Code</mat-label>
  <input matInput [(ngModel)]="inputCode" />
  <mat-hint class="mat-error" *ngIf="isError">{{ errorMessage }}</mat-hint>
</mat-form-field>
<div mat-dialog-actions>
  <button mat-button (click)="onNoClick()">Cancel</button>
  <button mat-button (click)="joinProject()" cdkFocusInitial>
    Join Project!
  </button>
</div>
